INSERT INTO `ea_guji_books` VALUES ('1147', '000aef8cf018600a5a36dc675ced0386', '11', '269', '268', '527', '中央战时工作干部训练团四周年纪念特刊', '中央战时工作干部训练团四周年纪念特刊', '', '[\"D693.61\"]', '1943', 'C:\\Users\\Administrator\\Desktop\\hello_word\\sources', '', '3c45b90bc6cbad063278cbc76233e04e.jpg', '干训刊物。本刊主要论述国民党战时工作干部的训练和教育方法、方针，报道该团团务及成立四年来大事纪要。其中团务概况包括组织、行政计划、教务、训育、军事管理、事务管理、党团活动、毕业生联络等方面。', '0', '191', '1', '', '[\"中央战时工作干部训练团四周年纪念特刊\"]', '[\"A0126\"]', 'Z', '', '[刊期不详]', '中央战时工作干部训练团编辑 [并发行]', '[出版地不详]', 'CQL0363/0363', '干训刊物。本刊主要论述国民党战时工作干部的训练和教育方法、方针，报道该团团务及成立四年来大事纪要。其中团务概况包括组织、行政计划、教务、训育、军事管理、事务管理、党团活动、毕业生联络等方面。', null, null, null);
