INSERT INTO `ea_guji_books` VALUES ('1190', '00f72331f0ce8c9424ecaab19caba4d9', '11', '270', '268', '527', '西北实业月刊', '西北实业月刊', '西北实业公司编审委员会编辑', '', '0', 'C:\\Users\\Administrator\\Desktop\\hello_word\\sources', '', '', '工业经济刊物。旨在使国人认识西北、重视西北。内容有关于实业之政令训话论著及建设论著、各厂所现况改进及复员经过、各种制作之心得及方法暨技术之改进与发明、有关实业之各种翻译、公司营业实况、有关实业之文艺创作等。作者大都系西北实业公司职工。', '0', '191', '1', '', '[\"西北实业月刊\",\"North-Western Industry\"]', '[\"A0109\"]', 'X', '西北实业公司编审委员会编辑', '月刊', '', '太原', '上图(4112)2137; 4112/15770-72', '工业经济刊物。旨在使国人认识西北、重视西北。内容有关于实业之政令训话论著及建设论著、各厂所现况改进及复员经过、各种制作之心得及方法暨技术之改进与发明、有关实业之各种翻译、公司营业实况、有关实业之文艺创作等。作者大都系西北实业公司职工。', null, null, null);
INSERT INTO `ea_guji_books` VALUES ('1191', '00fe2f94d69633beb7d77443bc5d1006', '11', '270', '268', '527', '革新月刊', '革新月刊', '吴伯卿等主编;  革新月刊社出版', '', '0', 'C:\\Users\\Administrator\\Desktop\\hello_word\\sources', '', '', '政治刊物。本刊以团结革命同志研究革命理论与问题，促进党政革新运动为宗旨。内容包括登载三民主义的学术性著述，政纲政策政治法令之研究报告、实施方案或改进意见，党政建设事业方面的建议，国内外政治情势之分析与评述，以及一部分短小精悍之文艺小品等。', '0', '191', '1', '', '[\"革新月刊\"]', '[\"A0111\"]', 'G', '吴伯卿等主编;  革新月刊社出版', '月刊', '', '长沙', '上图6750/23844-57', '政治刊物。本刊以团结革命同志研究革命理论与问题，促进党政革新运动为宗旨。内容包括登载三民主义的学术性著述，政纲政策政治法令之研究报告、实施方案或改进意见，党政建设事业方面的建议，国内外政治情势之分析与评述，以及一部分短小精悍之文艺小品等。', null, null, null);
