INSERT INTO `ea_guji_books` VALUES ('1207', '01464c698f69a538f6db2b17b807ec33', '11', '274', '268', '527', '土壤专报', '土壤专报', '实业部地质调查所,(国立)北平研究院地质学研究所编辑;  国立北平研究院地质研究所(印行)?', '', '0', 'C:\\Users\\Administrator\\Desktop\\hello_word\\sources', '', '', '1930年中华教育文化基金董事会决议委托地质调查所举办全国土壤调查，地质调查，即筹备成立了土壤研究室，聘请专员开始工作，所得结果就在土壤专报中发表。', '0', '191', '1', '', '[\"土壤专报\",\"Soil Bulletin\"]', '[\"A0102\",\"A0103\"]', 'T', '实业部地质调查所,(国立)北平研究院地质学研究所编辑;  国立北平研究院地质研究所(印行)?', '不定期', '', '北京', '上图(437)199; 437/1647-1654', '1930年中华教育文化基金董事会决议委托地质调查所举办全国土壤调查，地质调查，即筹备成立了土壤研究室，聘请专员开始工作，所得结果就在土壤专报中发表。', null, null, null);
