INSERT INTO `ea_guji_books` VALUES ('1213', '015ee1c8055c61924206bac16522031b', '11', '274', '268', '527', '茂名县政季刊', '茂名县政季刊', '茂名县政府庶务处 [发行]', '[\"D693.62\"]', '1934', 'C:\\Users\\Administrator\\Desktop\\hello_word\\sources', '', '', '地方行政刊物。介绍县政工作，传播政务消息，调查社会状况，研究现代行政方案，推行本省三年施政计划。', '0', '191', '1', '', '[\"茂名县政季刊\"]', '[\"A0124\",\"A0302\"]', 'M', '茂名县政府庶务处 [发行]', '季刊', '茂名县政府县政季刊编辑处编辑', '茂名县(广州)', 'GWJ0201/0201', '地方行政刊物。介绍县政工作，传播政务消息，调查社会状况，研究现代行政方案，推行本省三年施政计划。', null, null, null);
INSERT INTO `ea_guji_books` VALUES ('1214', '0171798da198c47c80497e701526265f', '11', '274', '268', '527', '前进中国', '前进中国', '郑业瑞 [发行人]', '[\"D693.09\"]', '1946', 'C:\\Users\\Administrator\\Desktop\\hello_word\\sources', '', '', '1946年创刊于重庆，月刊，由前进中国月刊出版社编辑出版发行，社址位于重庆市海棠溪盐店湾24号，郑业瑞担任总发行人，王裕凯、谢成恩、张立担任该刊物主要编辑和作者。该刊属于政治性刊物，刊物标题由当时著名的政治家，书法家于右任亲笔题写。刊行一期后随即停刊，具体停刊原因以及时间不详，馆藏创刊号一期。该刊属于政治性刊物，以反对贪污腐败、投机取巧，提倡国民积极进取，实现真前进为创刊宗旨，该刊创刊之时正是抗日战争胜利后的第一年，国内各界百废待兴，各党派冲突激烈，因而该刊在文章的选择上更倾向于政治化。该刊投稿大多来自于读者投稿，刊文内容丰富，文章形式不限，主要刊登有关国民党宪法、教育方面的政治评论、时事评论、人物传记等，但同时发表了不少反苏反共的言论。该刊常设有专栏、时代呼声、人物介绍、资料数篇、青年园地等特色栏目。在“专栏”一栏中主要对当前热议较多的国际关系、宗教问题进行讨论，对新出台的国际法令进行全新解读，或是对三民主义中所提到的教育相关的内容进行探讨，代表篇目有《印度独立声中的回教同盟》、《三民主义与大学教育》、《没有东三省就没有中国》；“时代呼声”一栏中的文章大多为青年读者投稿，抒发了当代青年人对时代的新认知，对学生运动的看法，尤其是对当时党派斗阵激烈这一现实情况进行了细致的梳理和严谨的分析，代表篇目有《青年中国的青年运动》、《反对各党各派破坏民主的非法决议》；“人物介绍”一栏每期选取一位国内外军政要员进行详细介绍，代表载文有《基楠将军传》；“资料数篇”一刊主要为该刊物编辑对现阶段所收集到的各类资料进行整理并予以登出，但这类资料有着明显的反共反苏的特点，其真实性有待考证。代表内容有《苏俄侵略中国史》、《中国现有各党派小传》《新闻大地小统计》。该刊虽然现存期数较少，但其中所刊载的文章内容丰富，题材多样，对于研究建国前期国内各党派之间的斗争提供了翔实的一手史料，由于该刊多为青年人投稿，且受众多为青年学生，因而对于研究同时期青年人的思想波动，心态变化都提供了新的窗口。', '0', '191', '1', '', '[\"前进中国\"]', '[\"A0126\"]', 'Q', '郑业瑞 [发行人]', '月刊', '华联同乐会学术科出版组,华联同乐会扩大征求委员会宣传股合编 [并发行]', '重庆', 'CQL0504/0504', '1946年创刊于重庆，月刊，由前进中国月刊出版社编辑出版发行，社址位于重庆市海棠溪盐店湾24号，郑业瑞担任总发行人，王裕凯、谢成恩、张立担任该刊物主要编辑和作者。该刊属于政治性刊物，刊物标题由当时著名的政治家，书法家于右任亲笔题写。刊行一期后随即停刊，具体停刊原因以及时间不详，馆藏创刊号一期。该刊属于政治性刊物，以反对贪污腐败、投机取巧，提倡国民积极进取，实现真前进为创刊宗旨，该刊创刊之时正是抗日战争胜利后的第一年，国内各界百废待兴，各党派冲突激烈，因而该刊在文章的选择上更倾向于政治化。该刊投稿大多来自于读者投稿，刊文内容丰富，文章形式不限，主要刊登有关国民党宪法、教育方面的政治评论、时事评论、人物传记等，但同时发表了不少反苏反共的言论。该刊常设有专栏、时代呼声、人物介绍、资料数篇、青年园地等特色栏目。在“专栏”一栏中主要对当前热议较多的国际关系、宗教问题进行讨论，对新出台的国际法令进行全新解读，或是对三民主义中所提到的教育相关的内容进行探讨，代表篇目有《印度独立声中的回教同盟》、《三民主义与大学教育》、《没有东三省就没有中国》；“时代呼声”一栏中的文章大多为青年读者投稿，抒发了当代青年人对时代的新认知，对学生运动的看法，尤其是对当时党派斗阵激烈这一现实情况进行了细致的梳理和严谨的分析，代表篇目有《青年中国的青年运动》、《反对各党各派破坏民主的非法决议》；“人物介绍”一栏每期选取一位国内外军政要员进行详细介绍，代表载文有《基楠将军传》；“资料数篇”一刊主要为该刊物编辑对现阶段所收集到的各类资料进行整理并予以登出，但这类资料有着明显的反共反苏的特点，其真实性有待考证。代表内容有《苏俄侵略中国史》、《中国现有各党派小传》《新闻大地小统计》。该刊虽然现存期数较少，但其中所刊载的文章内容丰富，题材多样，对于研究建国前期国内各党派之间的斗争提供了翔实的一手史料，由于