INSERT INTO `ea_guji_books` VALUES ('1287', '02702f6280423200a042c1ecac9f7c78', '11', '271', '271', '527', '追击', '追击', '', '[\"D693\"]', '1933', 'C:\\Users\\Administrator\\Desktop\\hello_word\\sources', '', '', '时政刊物。本刊主要登载国内外时事评论文章，分析国际形势，普及军事知识，也登载部分士兵的文章。栏目有时评、专载、军事、文艺、训示、随笔等。载文有“东北日阀内讧日烈”、“中国必亡于苟安”、“苏俄加入国联问题”等。', '0', '191', '1', '', '[\"追击\"]', '[\"A0130\",\"A0302\"]', '', '', '[刊期不详]', '国民革命军陆军独立第四师政治训练处编辑 [并发行]', '广州', 'GWJ1484/1484', '时政刊物。本刊主要登载国内外时事评论文章，分析国际形势，普及军事知识，也登载部分士兵的文章。栏目有时评、专载、军事、文艺、训示、随笔等。载文有“东北日阀内讧日烈”、“中国必亡于苟安”、“苏俄加入国联问题”等。', null, null, null);
