INSERT INTO `ea_guji_books` VALUES ('1310', '02efe249741e9c4ef8d2e23928789d02', '11', '271', '271', '527', '吴江妇女', '吴江妇女', '江苏妇女月刊社编辑', '', '0', 'C:\\Users\\Administrator\\Desktop\\hello_word\\sources', '', '', '妇女刊物。本刊研究妇女问题，提倡妇女运动，报道各地妇女团体、妇女运动消息，载文悼念“北京为爱国惨死的女烈士”、广州“三八”纪念大会详记、上海各界妇联会为“五卅”纪念日告同胞书等。', '0', '191', '1', '', '[\"吴江妇女\"]', '[\"A0109\"]', 'W', '江苏妇女月刊社编辑', '月刊', '', '上海', '上图4942/18232-33', '妇女刊物。本刊研究妇女问题，提倡妇女运动，报道各地妇女团体、妇女运动消息，载文悼念“北京为爱国惨死的女烈士”、广州“三八”纪念大会详记、上海各界妇联会为“五卅”纪念日告同胞书等。', null, null, null);
INSERT INTO `ea_guji_books` VALUES ('1311', '02f1b88e8aad062be441c86348ba800f', '11', '271', '271', '527', '文学杂志(北平)', '文学杂志(北平)', '文学杂志社编辑;  西北书局 [发行]', '', '0', 'C:\\Users\\Administrator\\Desktop\\hello_word\\sources', '', '', '北平左翼作家联盟机关刊物之一。以发表小说、诗歌、剧本、散文为主，兼有文学评论和文坛消息。曾得到鲁迅、茅盾、郑振铎等著名作家的支持。作品较注重反映社会现实问题，提倡无产阶级革命文学，批判自由主义思潮。主要撰稿人有：宋之的、丁玲、张天翼等。', '0', '191', '1', '', '[\"文学杂志(北平)\"]', '[\"A0102\"]', 'W', '文学杂志社编辑;  西北书局 [发行]', '月刊', '', '北平', '上图(1311)3727,005177-73', '北平左翼作家联盟机关刊物之一。以发表小说、诗歌、剧本、散文为主，兼有文学评论和文坛消息。曾得到鲁迅、茅盾、郑振铎等著名作家的支持。作品较注重反映社会现实问题，提倡无产阶级革命文学，批判自由主义思潮。主要撰稿人有：宋之的、丁玲、张天翼等。', null, null, null);
