INSERT INTO `ea_guji_books` VALUES ('1317', '02f9acd86fec9ddccd88b9e2f40a0992', '11', '271', '271', '527', '台湾人报半月刊', '台湾人报半月刊', '卢启予 [发行人]', '[\"C55\"]', '1948', 'C:\\Users\\Administrator\\Desktop\\hello_word\\sources', '', '', '1948年10月在台北创刊，半月刊，由吕正之任编辑、卢启予任发行人，停刊日期不详，属于社科综合刊物。该刊主要撰稿人有东方既白、良知、斗星、嘉宝、形天等。主要栏目不详，见存创刊号。本刊站在台湾六百万人民的立场，阐述民众思想，内容不限于台湾本省，也不限于过去的、目前的与历史的，有许多是日治时期被封锁的祖国文化和世界文化，报导有价值的、值得欣赏的文艺作品。陈以益为该刊题祝词：今日我国之报众矣，然多官报，而少人报，至今在台湾有人报之设，诚人类可喜之事也。载文有《台省当局传有变动吴国桢调主台》、《台湾航业公司内幕》、《汉奸张桐匿居在台》、《奇形的销金窟——香港》、《化险为夷的陈果夫》等。并刊登《台湾的老虎是谁》一文，指出台湾当时的两种“老虎”，一种是“土产老虎”，指台湾本地的政府官员；另一种是“飞来老虎”，即抗战后国民党从大陆派去台湾执行光复任务的官员，该文文笔犀利，揭露了当时国民党官员的腐败剥削的本质。特约通讯《台湾的私生子——澎湖》称澎湖是台湾的私生子，并阐明了澎湖小岛是日本侵犯南华的根据地，有极大的造船厂和最大的深港，是国防上的重地，也是东南海上的咽喉。该刊内容丰富，文章多揭示政事的腐败，声明站在人民的立场说话，是一份相对公正的报刊。', '0', '191', '1', '', '[\"台湾人报半月刊\"]', '[\"A0118\"]', 'T', '卢启予 [发行人]', '半月刊', '吕正之编辑', '台北', '12139/5721_1', '1948年10月在台北创刊，半月刊，由吕正之任编辑、卢启予任发行人，停刊日期不详，属于社科综合刊物。该刊主要撰稿人有东方既白、良知、斗星、嘉宝、形天等。主要栏目不详，见存创刊号。本刊站在台湾六百万人民的立场，阐述民众思想，内容不限于台湾本省，也不限于过去的、目前的与历史的，有许多是日治时期被封锁的祖国文化和世界文化，报导有价值的、值得欣赏的文艺作品。陈以益为该刊题祝词：今日我国之报众矣，然多官报，而少人报，至今在台湾有人报之设，诚人类可喜之事也。载文有《台省当局传有变动吴国桢调主台》、《台湾航业公司内幕》、《汉奸张桐匿居在台》、《奇形的销金窟——香港》、《化险为夷的陈果夫》等。并刊登《台湾的老虎是谁》一文，指出台湾当时的两种“老虎”，一种是“土产老虎”，指台湾本地的政府官员；另一种是“飞来老虎”，即抗战后国民党从大陆派去台湾执行光复任务的官员，该文文笔犀利，揭露了当时国民党官员的腐败剥削的本质。特约通讯《台湾的私生子——澎湖》称澎湖是台湾的私生子，并阐明了澎湖小岛是日本侵犯南华的根据地，有极大的造船厂和最大的深港，是国防上的重地，也是东南海上的咽喉。该刊内容丰富，文章多揭示政事的腐败，声明站在人民的立场说话，是一份相对公正的报刊。', null, null, null);
