INSERT INTO `ea_guji_books` VALUES ('1346', '03893588895e0d2e9e558a2cfba48a3e', '11', '273', '271', '527', '山东法学季刊', '山东法学季刊', '', '[\"D90\"]', '1937', 'C:\\Users\\Administrator\\Desktop\\hello_word\\sources', '', '', '法学刊物。本刊本着发扬民族文化之精神研究法学，以改进法制为宗旨。栏目有论著、专载、译述、附录等。载文有“施行法币与社会经济之影响”、“中国之制宪问题”、“中国缓刑制度之检讨”等。', '0', '191', '1', '', '[\"山东法学季刊\"]', '[\"A0130\"]', '', '', '季刊', '法学会山东省分会编辑 [并发行]', '山东', 'GT0164/0164', '法学刊物。本刊本着发扬民族文化之精神研究法学，以改进法制为宗旨。栏目有论著、专载、译述、附录等。载文有“施行法币与社会经济之影响”、“中国之制宪问题”、“中国缓刑制度之检讨”等。', null, null, null);
INSERT INTO `ea_guji_books` VALUES ('1347', '03899caf6cdd759d2fc3b8cf091cfe8c', '11', '273', '271', '527', '西村月报', '西村月报', '黄希周 [发行人]', '[\"C539\"]', '1932', 'C:\\Users\\Administrator\\Desktop\\hello_word\\sources', '', '', '本刊于1929年在广州创刊，收录至1941年10月第137期，现馆藏第36期。黄拜言担纲本刊的总编辑，黄希周为发行人。逐页题名《西村月刊》。月刊，综合性刊物。所设栏目：论著、群言、新闻、文苑、文录、诗录、公函，布告等。黄拜言、黄爱类、明英等为主要撰稿人。  本刊所载内容主要有政治评论、校务情况，还刊有诗歌等。主要载文有《在国际所立场上对日本承认满洲伪国之批评》、《杜诗研究》。“论著”一栏，载有《在国际所立场上对日本承认满洲伪国之批评》，讲述了日本侵略中国是十几年来处心积虑经营的结果，建立伪满洲国是日本假民族自决的美名，以避国际间的责难。进而，作者分析了满洲伪组织建立的背景，指出满洲伪组织没有独立国家成立的要件，所以日本承认满洲伪国实违反国际公法，满洲国是实为日本的傀儡，对这种行为应该给以严厉的制裁。“群言”一栏刊登了关于改革绍宪校务和乡公所催收团费的事宜。“新闻”栏目包括“校务”、“堡闻”、“邻近新闻”三部分，新闻内容较琐碎，既有车祸报道，又有盗窃的记录，反映了该地区的社会生活；“邻近新闻”详细报道了关于白沙地区的情况，如新白沙建筑市场委员会、新白沙投充市场情形、白沙沙洲建桥工程近况，白沙区药界庆祝台山国医支管成立等活动。“文苑”一栏，刊有《杜诗研究》一文，认为杜诗的抒情之作，虽然不是全部写实，但用的是写实的笔调，形容得当，带有几分写实意味。本刊内容丰富，既有政治评论、文学研究，又有新闻消息、群言群策、诗歌等，这对了解广东地方社会生活及整个社会状况有一定帮助。', '0', '191', '1', '', '[\"西村月报\"]', '[\"A0119\",\"A0302\"]', 'X', '黄希周 [发行人]', '月刊', '黄拜言总编辑', '广州', '04126/15822', '本刊于1929年在广州创刊，收录至1941年10月第137期，现馆藏第36期。黄拜言担纲本刊的总编辑，黄希周为发行人。逐页题名《西村月刊》。月刊，综合性刊物。所设栏目：论著、群言、新闻、文苑、文录、诗录、公函，布告等。黄拜言、黄爱类、明英等为主要撰稿人。  本刊所载内容主要有政治评论、校务情况，还刊有诗歌等。主要载文有《在国际所立场上对日本承认满洲伪国之批评》、《杜诗研究》。“论著”一栏，载有《在国际所立场上对日本承认满洲伪国之批评》，讲述了日本侵略中国是十几年来处心积虑经营的结果，建立伪满洲国是日本假民族自决的美名，以避国际间的责难。进而，作者分析了满洲伪组织建立的背景，指出满洲伪组织没有独立国家成立的要件，所以日本承认满洲伪国实违反国际公法，满洲国是实为日本的傀儡，对这种行为应该给以严厉的制裁。“群言”一栏刊登了关于改革绍宪校务和乡公所催收团费的事宜。“新闻”栏目包括“校务”、“堡闻”、“邻近新闻”三部分，新闻内容较琐碎，既有车祸报道，又有盗窃的记录，反映了该地区的社会生活；“邻近新闻”详细报道了关于白沙地区的情况，如新白沙建筑市场委员会、新白沙投充市场情形、白沙沙洲建桥工程近况，白沙区药界庆祝台山国医支管成立等活动。“文苑”一栏，刊有《杜诗研究》一文，认为杜诗的抒情之作，虽然不是全部写实，但用的是写实的笔调，形容得当，带有几分写实意味。本刊内容丰富，既有政治评论、文学研究，又有新闻消息、群言群策、诗歌等，这对了解广东地方社会生活及整个社会状况有一定帮助。', null, null, null);
