INSERT INTO `ea_guji_books` VALUES ('1362', '03ab36e138dd1bd3c2bc449af64b28d4', '11', '273', '271', '527', '民众教育月刊(四川泸县)', '民众教育月刊(四川泸县)', '四川合江民众教育馆民众教育月刊编辑部编辑', '', '0', 'C:\\Users\\Administrator\\Desktop\\hello_word\\sources', '', '', '民众教育刊物。该刊以倡导民众教育，激发民族意识，培养民众力量，增进民众知能，改善民众生活，以促进地方建设为主旨。内容有民众教育专著、妇女教育动态、教育简讯以及该馆消息、概况与科学杂谈。', '0', '191', '1', '', '[\"民众教育月刊(四川泸县)\"]', '[\"A0109\"]', 'M', '四川合江民众教育馆民众教育月刊编辑部编辑', '月刊', '', '泸县(四川)', '上图(3063)1669', '民众教育刊物。该刊以倡导民众教育，激发民族意识，培养民众力量，增进民众知能，改善民众生活，以促进地方建设为主旨。内容有民众教育专著、妇女教育动态、教育简讯以及该馆消息、概况与科学杂谈。', null, null, null);
INSERT INTO `ea_guji_books` VALUES ('1363', '03adfffcedf6bce80d20b36d181e934f', '11', '273', '271', '527', '江苏货物税通讯', '江苏货物税通讯', '', '[\"F812.753\"]', '1946', 'C:\\Users\\Administrator\\Desktop\\hello_word\\sources', '', '', '1946年7月1日创刊于江苏，至1947年6月底第2卷5/6期停刊。由财政部江苏区货物税局编辑并发行。其他题名《江苏区货物税通讯》。月刊，40年代税务类专门刊物。主要撰稿人有傅谦之、王修经、费守纶、蒋绍金等。该报刊旨在阐述税务理论，宣传税务法令、税务人员守则、报道该局工作情况。刊载文章既包含指导税务工作的专门性论著，如《货物税从价征收之理论与实际》、《新货物税稽征办法述要》等；还包含税务工作人员在工作中发现的问题，如《江苏区货物税源概述》、《改进税收稽征之我见》。《怎样做一个廉洁的税务员》一文分析了税务员的意义和税务员的责任。提出培养廉洁的税务员队伍，除了要求税务员本身提高修养，要节俭、有抱负、负责任等，还应从制度层面为税务员提供良好的客观环境，如提高待遇、保障位置、养成人民守法精神、彻底铲除封建势力、厉行考核制度等。还刊载了《货物税条例》、《国产烟酒类税务条例》以及该局人事安排与调动等法令文牍。第5期为财政部江苏区货物税局成立一周年纪念特刊。刊载了各界人员的题字、该局职员的团体照片、各项税务工作的统计图表等。《一年来工作制回顾》一文记录了该局在1946年一年间的工作内容，包含局址筹设及迁镇经过、各分局成立经过及派员接收情形、人事甄用管理、初步调查税源与货品市价、举行业务会议、新税调查与开征、预算分配与税收概况等。《江苏货物税通讯》的出版发行为基层税务工作人员及时了解最新法令、学习税务管理方法提供了便利，也为后人研究民国晚期江苏省的税务状况提供了丰富的资料。', '0', '191', '1', '', '[\"江苏货物税通讯\"]', '[\"A0119\"]', 'J', '', '月刊', '财政部江苏区货物税局编辑 [并发行]', '江苏', '03897/14753_55', '1946年7月1日创刊于江苏，至1947年6月底第2卷5/6期停刊。由财政部江苏区货物税局编辑并发行。其他题名《江苏区货物税通讯》。月刊，40年代税务类专门刊物。主要撰稿人有傅谦之、王修经、费守纶、蒋绍金等。该报刊旨在阐述税务理论，宣传税务法令、税务人员守则、报道该局工作情况。刊载文章既包含指导税务工作的专门性论著，如《货物税从价征收之理论与实际》、《新货物税稽征办法述要》等；还包含税务工作人员在工作中发现的问题，如《江苏区货物税源概述》、《改进税收稽征之我见》。《怎样做一个廉洁的税务员》一文分析了税务员的意义和税务员的责任。提出培养廉洁的税务员队伍，除了要求税务员本身提高修养，要节俭、有抱负、负责任等，还应从制度层面为税务员提供良好的客观环境，如提高待遇、保障位置、养成人民守法精神、彻底铲除封建势力、厉行考核制度等。还刊载了《货物税条例》、《国产烟酒类税务条例》以及该局人事安排与调动等法令文牍。第5期为财政部江苏区货物税局成立一周年纪念特刊。刊载了各界人员的题字、该局职员的团体照片、各项税务工作的统计图表等。《一年来工作制回顾》一文记录了该局在1946年一年间的工作内容，包含局址筹设及迁镇经过、各分局成立经过及派员接收情形、人事甄用管理、初步调查税源与货品市价、举行业务会议、新税调查与开征、预算分配与税收概况等。《江苏货物税通讯》的出版发行为基层税务工作人员及时了解最新法令、学习税务管理方法提供了便利，也为后人研究民国晚期江苏省的税务状况提供了丰富的资料。', null, null, null);
