INSERT INTO `ea_guji_books` VALUES ('1404', '046f3e7f379ff5ccd3c68bf885bfe389', '11', '268', '268', '527', '福建省合作通讯', '福建省合作通讯', '', '[\"F329.57\"]', '1937', 'C:\\Users\\Administrator\\Desktop\\hello_word\\sources', '', '', '1937年创刊于福建福州，月刊，由福建省合作事业管理处编辑出版，停刊时间及原因不详，现存1938年第6、7期合刊，第8期，第11、12期合刊。 该刊旨在探讨战时合作事业的各种问题，公布中央及地方颁布的有关命令法规、会务记录，报道福建省内合作消息和一般的动态新闻。栏目有论著、农业知识、特载、训令、章则法规、工作报告、工作提示、人事动态、合作情报等。撰稿人有王世浩、郑奕盛、陈希诚、刘青山、张向岗、樊立仁、汪东本、汪石芳、王世颖等。该刊的合作内容有关于抗战建国时期的合作事业发展的讨论、合作事业相关的章则规程、福建省第地方合作事业的推进情况等。“论著”一栏是对合作事业发表的评论，载文有《合作与抗战建国》、《怎样训练农村干部人员》、《论战时合作组织与训练》；“农业知识”一栏，是普及性质的文章，如《堆肥之新的研究》、《鸡饲料之配合与调制》；“特载”一栏是专载的号召性文章，如《告合作同仁书》；“章则法规”一栏是合作事业中的规程条例，载文有《合作社联合社经营要则》、《各县办理讲习会注意事项》等；“合作情报”即使地方合作事业的报道，又是对合作工作的自我认识，刊有《福鼎合作事业日见发展》、《各县合作节盛况摘录》等。其他文章还有《合作社社员值日暂行办法》、《十二月份人事调动简明表》等。另外“选载”一栏是对当时国际战局的记录和报道。该刊虽为合作事业刊物，却不拘泥于合作事业的研究上，还普及了农业知识，对于福建省地方经济、农业研究意义重大，作为当时全国合作事业的一部分，可以为研究当时整个合作事业作补充资料。', '0', '191', '1', '', '[\"福建省合作通讯\"]', '[\"A0130\"]', '', '', '月刊', '福建省合作事业管理处编辑 [并发行]', '福建省', 'FD0190/0190', '1937年创刊于福建福州，月刊，由福建省合作事业管理处编辑出版，停刊时间及原因不详，现存1938年第6、7期合刊，第8期，第11、12期合刊。 该刊旨在探讨战时合作事业的各种问题，公布中央及地方颁布的有关命令法规、会务记录，报道福建省内合作消息和一般的动态新闻。栏目有论著、农业知识、特载、训令、章则法规、工作报告、工作提示、人事动态、合作情报等。撰稿人有王世浩、郑奕盛、陈希诚、刘青山、张向岗、樊立仁、汪东本、汪石芳、王世颖等。该刊的合作内容有关于抗战建国时期的合作事业发展的讨论、合作事业相关的章则规程、福建省第地方合作事业的推进情况等。“论著”一栏是对合作事业发表的评论，载文有《合作与抗战建国》、《怎样训练农村干部人员》、《论战时合作组织与训练》；“农业知识”一栏，是普及性质的文章，如《堆肥之新的研究》、《鸡饲料之配合与调制》；“特载”一栏是专载的号召性文章，如《告合作同仁书》；“章则法规”一栏是合作事业中的规程条例，载文有《合作社联合社经营要则》、《各县办理讲习会注意事项》等；“合作情报”即使地方合作事业的报道，又是对合作工作的自我认识，刊有《福鼎合作事业日见发展》、《各县合作节盛况摘录》等。其他文章还有《合作社社员值日暂行办法》、《十二月份人事调动简明表》等。另外“选载”一栏是对当时国际战局的记录和报道。该刊虽为合作事业刊物，却不拘泥于合作事业的研究上，还普及了农业知识，对于福建省地方经济、农业研究意义重大，作为当时全国合作事业的一部分，可以为研究当时整个合作事业作补充资料。', null, null, null);
INSERT INTO `ea_guji_books` VALUES ('1405', '047d0d2001cb666bdf59ce5e92e39d63', '11', '268', '268', '527', '战士生活', '战士生活', '', '[\"D693.74\",\"K265.2\"]', '1934', 'C:\\Users\\Administrator\\Desktop\\hello_word\\sources', '', '', '国民党派系刊物。本刊的使命即在发扬我们救国救党的意志和主张，一致努力于为国家求生，为民族图存，为本党重光，为三民主义实现，向祸国篡党的南京军阀统治进攻。载文有“南京亲日派与欧美派斗争之复活”、“蒋介石在军事上的罪恶”、“军阀统治下的华北通道”等。', '0', '191', '1', '', '[\"战士生活\"]', '[\"A0124\"]', 'Z', '', '半月刊', '中国青年军人社编辑 [并发行]', '南京', '12950/45752', '国民党派系刊物。本刊的使命即在发扬我们救国救党的意志和主张，一致努力于为国家求生，为民族图存，为本党重光，为三民主义实现，向祸国篡党的南京军阀统治进攻。载文有“南京亲日派与欧美派斗争之复活”、“蒋介石在军事上的罪恶”、“军阀统治下的华北通道”等。', null, null, null);
