INSERT INTO `ea_guji_books` VALUES ('1412', '04934784f3840e895f95959f5f14215e', '11', '268', '268', '527', '佛教文艺', '佛教文艺', '南京毗卢佛学院 [发行]', '[\"B94\"]', '1944', 'C:\\Users\\Administrator\\Desktop\\hello_word\\sources', '', '', '该刊是南京佛教文艺社佛学刊物。本刊依纯佛教立场，载有关于佛教及现社会的评判，改进，理论的论著，文艺等内容。', '0', '191', '1', '', '[\"佛教文艺\"]', '[\"A0120\"]', 'F', '南京毗卢佛学院 [发行]', '月刊', '佛教文艺月刊社编辑', '南京', '16600/64661_63', '该刊是南京佛教文艺社佛学刊物。本刊依纯佛教立场，载有关于佛教及现社会的评判，改进，理论的论著，文艺等内容。', null, null, null);
