INSERT INTO `ea_guji_books` VALUES ('1415', '049d93ca88070704a149fd5ae833123e', '11', '268', '268', '527', '世界：战事画报', '世界：战事画报', '交统合作社 [出版]', '[\"E1\"]', '1940', 'C:\\Users\\Administrator\\Desktop\\hello_word\\sources', '', '', '1940年在上海创刊，月刊，发行人不详，停刊日期不详，属于时事画刊。该刊由陈瀕舟、丁禾菲、林秉忠编辑，主要撰稿人不详。主要栏目有巴尔干半岛的危机、漫画战、英陆军跨海东征等。该刊以介绍世界战事行进状态为主旨，反映欧洲英德美等国情况，评论战事进行趋势，分析我国在当时国际环境中之地位，也刊载中国军队抗日照片与其它国家的生活图片，由《世界画报》继承。所载内容有《德军集结荷兰边境》、《德空军活跃英法境》、《法国报纸上的讽刺漫画》、《肢解下的波兰》、《大战历史重演德国二十五年前后》、《空袭前夜花絮》、《纳粹进军新路线》、《法空中女救护队》、《英王视察前线》、《英远征军赴法前线调防》、《飞机市场加速生产》等有关战争局势的照片，载文《海上霸王的总枢纽英国海军部》、《全国皆兵：英妇女界总动员》、《连天烽火话芬兰》、《北海封锁线》、《坦克争夺战》、《昨日之梦》等，并附有英文对照，配有照片，全面的展现了欧洲战场的实况。', '0', '191', '1', '', '[\"世界：战事画报\",\"World In Pictures：The Second World War Features\"]', '[\"A0119\"]', 'S', '交统合作社 [出版]', '月刊', '陈瀕舟,丁禾菲,林秉忠编辑', '上海', '14852/57147_48', '1940年在上海创刊，月刊，发行人不详，停刊日期不详，属于时事画刊。该刊由陈瀕舟、丁禾菲、林秉忠编辑，主要撰稿人不详。主要栏目有巴尔干半岛的危机、漫画战、英陆军跨海东征等。该刊以介绍世界战事行进状态为主旨，反映欧洲英德美等国情况，评论战事进行趋势，分析我国在当时国际环境中之地位，也刊载中国军队抗日照片与其它国家的生活图片，由《世界画报》继承。所载内容有《德军集结荷兰边境》、《德空军活跃英法境》、《法国报纸上的讽刺漫画》、《肢解下的波兰》、《大战历史重演德国二十五年前后》、《空袭前夜花絮》、《纳粹进军新路线》、《法空中女救护队》、《英王视察前线》、《英远征军赴法前线调防》、《飞机市场加速生产》等有关战争局势的照片，载文《海上霸王的总枢纽英国海军部》、《全国皆兵：英妇女界总动员》、《连天烽火话芬兰》、《北海封锁线》、《坦克争夺战》、《昨日之梦》等，并附有英文对照，配有照片，全面的展现了欧洲战场的实况。', null, null, null);
INSERT INTO `ea_guji_books` VALUES ('1416', '04a8eeaa025466871e5d619a8f22980a', '11', '268', '268', '527', '文萃', '文萃', '文萃社 [并出版]', '', '0', 'C:\\Users\\Administrator\\Desktop\\hello_word\\sources', '', '', '时政刊物。抗战胜利后国民党统治区进步民主人士主办。该刊政治倾向鲜明，站在以中共为首的人民民主力量一边，反对内战，反对独裁，揭露美帝国主义和国民党反动派发动内战，实行独裁统治的罪行，对国统区人民反饥饿,反内战,争取民主的斗争进行大量报道，对重大事件进行评论。', '0', '191', '1', '', '[\"文萃\"]', '[\"A0102\"]', 'W', '文萃社 [并出版]', '周刊', '', '上海', '上图(1218)604', '时政刊物。抗战胜利后国民党统治区进步民主人士主办。该刊政治倾向鲜明，站在以中共为首的人民民主力量一边，反对内战，反对独裁，揭露美帝国主义和国民党反动派发动内战，实行独裁统治的罪行，对国统区人民反饥饿,反内战,争取民主的斗争进行大量报道，对重大事件进行评论。', null, null, null);
