INSERT INTO `ea_guji_books` VALUES ('1425', '04d693f0c17b0ce68fe792620a0302e6', '11', '268', '268', '527', '新中华杂志', '新中华杂志', '', '[\"C55\"]', '1932', 'C:\\Users\\Administrator\\Desktop\\hello_word\\sources', '', '', '社会科学综合性刊物。本刊以忠实的立场，研究三民主义新社会建设的理论，唤起同胞努力从事建设新中华的工作。载文有“吾人对于国民会议之希望”、“国民道德问题”、“帝国主义之本来面目观”等。', '0', '191', '1', '', '[\"新中华杂志\"]', '[\"A0126\"]', 'X', '', '月刊', '新中华杂志社编辑 [并发行]', '南京', '10458/36739', '社会科学综合性刊物。本刊以忠实的立场，研究三民主义新社会建设的理论，唤起同胞努力从事建设新中华的工作。载文有“吾人对于国民会议之希望”、“国民道德问题”、“帝国主义之本来面目观”等。', null, null, null);
INSERT INTO `ea_guji_books` VALUES ('1426', '04d7714defb70962b2c4bdb63af8f83f', '11', '268', '268', '527', '妇女月刊', '妇女月刊', '陆翰芩等编辑;  妇女月刊社出版', '[\"I266\"]', '0', 'C:\\Users\\Administrator\\Desktop\\hello_word\\sources', '', '', '1941年9月在重庆创刊，该刊曾于1945年11月第4卷第6期停刊；又于1946年11月复刊，出版地改为南京，1948年11月第7卷第5期停刊。陆翰芩、林苑文、陆晶清等编辑，妇女月刊社出版。月刊，属于妇女文学刊物。主要撰稿人有宗白华、曹诚英、赖素心、吴越、寒梅、亚萍、寒波、彭道真等，设有妇女消息、论著、短评、通讯、特写、科学小品，文艺、家庭指导、读者园地、杂俎等栏目。该刊创办目的是开创一片让妇女同志们倾吐和控诉的园地，让封建的罪恶暴露出来；提供理论和常识供妇女同志工作研究与生活参考。载文多以女性为主题，探讨妇女参政、妇女贞操等相关话题，内容十分丰富，还有刊载联合国中妇女的地位问题，夫妇生活论等文章，介绍闽北、台湾、西藏、广西等地区妇女生活通讯、儿童性教、炊事常识和“多胎与遗传”、肥胖疗法及一些短小的文艺作品等。其中载文《救救孩子！为儿童节而作》、《改进小学教育的我见》、《大学招收女生始自北大：蔡元培先生倡导之功》等文章，分别从儿童教育、小学教育和大学教育等方面阐述了对教育的重视，尤其是蔡元培倡导大学招收女生，实为时代之进步，妇女教育之先导，意义重大。而《妇女消息：西班牙妇女联盟》、《蒋夫人访美》等则从国内外有重要影响力的事件或人物谈起，意在宣扬女性参政的必要性。文学方面则以刊登的流云小诗为主，包括《流云小诗：月落时》、《流云小诗：东海滨》等，文字清新雅致，意境优美。《沦落图：重庆妓女生活》、《娼妓制度与家庭》等文介绍了娼妓的相关情况。该刊所载内容充盈，论述深刻，文字平实，发刊时间长达8年，是民国时期较为重要的妇女文学刊物。', '0', '191', '1', '', '[\"妇女月刊\"]', '[\"A0112\"]', 'F', '陆翰芩等编辑;  妇女月刊社出版', '月刊', '', '重庆', '上图(9432)4508; 9432/34176-81', '1941年9月在重庆创刊，该刊曾于1945年11月第4卷第6期停刊；又于1946年11月复刊，出版地改为南京，1948年11月第7卷第5期停刊。陆翰芩、林苑文、陆晶清等编辑，妇女月刊社出版。月刊，属于妇女文学刊物。主要撰稿人有宗白华、曹诚英、赖素心、吴越、寒梅、亚萍、寒波、彭道真等，设有妇女消息、论著、短评、通讯、特写、科学小品，文艺、家庭指导、读者园地、杂俎等栏目。该刊创办目的是开创一片让妇女同志们倾吐和控诉的园地，让封建的罪恶暴露出来；提供理论和常识供妇女同志工作研究与生活参考。载文多以女性为主题，探讨妇女参政、妇女贞操等相关话题，内容十分丰富，还有刊载联合国中妇女的地位问题，夫妇生活论等文章，介绍闽北、台湾、西藏、广西等地区妇女生活通讯、儿童性教、炊事常识和“多胎与遗传”、肥胖疗法及一些短小的文艺作品等。其中载文《救救孩子！为儿童节而作》、《改进小学教育的我见》、《大学招收女生始自北大：蔡元培先生倡导之功》等文章，分别从儿童教育、小学教育和大学教育等方面阐述了对教育的重视，尤其是蔡元培倡导大学招收女生，实为时代之进步，妇女教育之先导，意义重大。而《妇女消息：西班牙妇女联盟》、《蒋夫人访美》等则从国内外有重要影响力的事件或人物谈起，意在宣扬女性参政的必要性。文学方面则以刊登的流云小诗为主，包括《流云小诗：月落时》、《流云小诗：东海滨》等，文字清新雅致，意境优美。《沦落图：重庆妓女生活》、《娼妓制度与家庭》等文介绍了娼妓的相关情况。该刊所载内容充盈，论述深刻，文字平实，发刊时间长达8年，是民国时期较为重要的妇女文学刊物。', null, null, null);
