INSERT INTO `ea_guji_books` VALUES ('1470', '059e639f930b17821210d7b248844362', '11', '268', '268', '527', '新闻评论', '新闻评论', '', '[\"D5\"]', '1945', 'C:\\Users\\Administrator\\Desktop\\hello_word\\sources', '', '', '时政政治评论刊物。主要对抗日战争胜利后的国内外局势进行报道和评论，内容以政治问题为主，如政治协商、反对内战、争取民主、学生运动等，其次为军事和经济方面的问题，也报道一些苏联情况和解放区见闻。', '0', '191', '1', '', '[\"新闻评论\"]', '[\"A0124\"]', 'X', '', '周刊,旬刊', '新闻评论社编辑 [并发行]', '北平', '10897/38225_26', '时政政治评论刊物。主要对抗日战争胜利后的国内外局势进行报道和评论，内容以政治问题为主，如政治协商、反对内战、争取民主、学生运动等，其次为军事和经济方面的问题，也报道一些苏联情况和解放区见闻。', null, null, null);
INSERT INTO `ea_guji_books` VALUES ('1471', '059ef8e79bdf443110f25b76d6ecae7a', '11', '268', '268', '527', '政衡', '政衡', '政衡月刊社编辑 [并发行]', '', '0', 'C:\\Users\\Administrator\\Desktop\\hello_word\\sources', '', '', '政治经济类刊物。本刊发刊宗旨是从事于实际问题的讨论，着重于批判现实，但是对于有根据的理论文字也兼收并蓄。刊载内容包括外交、教育、经济、社会等各方面的精辟文字叙述。复刊中还增加了学术专论、生活经验、中外通讯等内容。', '0', '191', '1', '', '[\"政衡\"]', '[\"A0111\"]', 'Z', '政衡月刊社编辑 [并发行]', '月刊', '', '南京', '上图(6684)3222; 6684/23584-85,92', '政治经济类刊物。本刊发刊宗旨是从事于实际问题的讨论，着重于批判现实，但是对于有根据的理论文字也兼收并蓄。刊载内容包括外交、教育、经济、社会等各方面的精辟文字叙述。复刊中还增加了学术专论、生活经验、中外通讯等内容。', null, null, null);
INSERT INTO `ea_guji_books` VALUES ('1472', '05a0183d5cc675442cabd2ddcb1dc1b0', '11', '268', '268', '527', '人钟月刊', '人钟月刊', '人钟月刊社编辑部主办;  申新第三纺织厂出版', '', '0', 'C:\\Users\\Administrator\\Desktop\\hello_word\\sources', '', '', '无锡申新第三纺织厂发行的纺织专业技术刊物。刊名“人钟”，为该厂棉纱产品商标。该刊“以研究纺织学术及谋工务之改进为宗旨”。刊登纺织界之评论及特要消息，纺织学理论及技术之研究著作，译述东西各国之纺织书籍，以及有研究参考价值的各种图表和照片。', '0', '191', '1', '', '[\"人钟月刊\"]', '[\"A0102\"]', 'R', '人钟月刊社编辑部主办;  申新第三纺织厂出版', '月刊', '', '无锡', '上图(229)98; 229/758', '无锡申新第三纺织厂发行的纺织专业技术刊物。刊名“人钟”，为该厂棉纱产品商标。该刊“以研究纺织学术及谋工务之改进为宗旨”。刊登纺织界之评论及特要消息，纺织学理论及技术之研究著作，译述东西各国之纺织书籍，以及有研究参考价值的各种图表和照片。', null, null, null);
INSERT INTO `ea_guji_books` VALUES ('1473', '05a3ede586adcb1800e1939cb21525e6', '11', '268', '268', '527', '海会旬刊', '海会旬刊', '', '[\"E271\"]', '1933', 'C:\\Users\\Administrator\\Desktop\\hello_word\\sources', '', '', '军事政论性刊物。本刊内容有论述有关国内外时事、政治、经济、社会等问题的论著，也有文艺与杂俎。载文有“读苏俄视察记以后”、“资本主义的盲肠炎与中国的肺痨病”、“一个曾经迷路的青年”等。', '0', '191', '1', '', '[\"海会旬刊\"]', '[\"A0126\"]', 'H', '', '旬刊', '中央陆军军官学校驻赣特别研究班海会旬刊社主办 [并出版]', '江西', '07709/28287', '军事政论性刊物。本刊内容有论述有关国内外时事、政治、经济、社会等问题的论著，也有文艺与杂俎。载文有“读苏俄视察记以后”、“资本主义的盲肠炎与中国的肺痨病”、“一个曾经迷路的青年”等。', null, null, null);
INSERT INTO `ea_guji_books` VALUES ('1474', '05aee2f48f28adecbd7d090dbae1128a', '11', '268', '268', '527', '简讯', '简讯', '', '[\"F552.6\"]', '1934', 'C:\\Users\\Administrator\\Desktop\\hello_word\\sources', '', '', '《简讯》，属航运企业刊物，由民生实业股份有限公司刊行，周刊，每周一出版，1926年5月10日创刊，出版地不详，1939年1月因重庆遭日军轰炸，印刷困难，遂暂时停刊，后于1943年6月14日复刊，[编者：《民生公司简讯复刊启示》，《简讯》1943年6月第696期，第1版。]。馆藏1943年第696、697、701、703、707、707、709、714、716、718期，1945年第789期。该刊第708期之前，一般每期2版，有中缝，自708期起采用双面排印，每期3到5版，取消中缝。内容方面，该刊由公告、通讯、专载、人事、简言、各部消息六个栏目组成。“公告”栏目，主要公布公司的重要事件及重要通函。重要事件的公告，如对“长航轮船各级仓位乘客伙食，自六月十日起，一律由乘客自理，由轮船公司代办”一事的公告。重要通函，则为公司内部的重要文件，如总协28号，对“主任副主任以上人员午后办公延长半小时至五点三十分止”的规定。“各部消息”，主要刊登公司各方面的新闻，如“卢总经理复职”，“民权轮因嘉陵江水暴涨受损”，“上游轮船运煤新规定”，“周会纪略”等消息。“专载”，主要刊载重要人物的讲话及运输方面的论著，刊中载有蒋委员对民族公司迁厂努力及运输艰辛的赞美之词，还有卢总经理载周会上的讲话。著作方面，刊中连载有译自Sidney Howard的《我们的商船队》的《海难与施救》。“人事”栏目，主要发布公司的人事变动信息，如“聘王企光先生为本公司运务处经理”、“民本轮代经理张慰松调民万轮经理”等信息。“简言”，主要刊登各方面的言论，如《卢总经理复职，同仁应有的认识》、《为什么要禁赌》、《人定胜天》等。此外，第708期之前的中缝部分，主要担负刊登公司启示的功能，载有《民生公司简讯复刊启示》、《本公司整理人事登记启示》等内容。《简讯》，是民生实业股份有限公司出版的一份企业刊物，该刊是民生公司管理公司内部事务的重要工具，也是外界了解公司详情的重要材料。该刊是研究民生公司的重要史料。', '0', '191', '1', '', '[\"短信\"]', '[\"A0126\"]', 'J', '', '周刊', '华联同乐会学术科出版组,华联同乐会扩大征求委员会宣传股合编 [并发行]', '[出版地不详]', 'CQL0489/0489', '《简讯》，属航运企业刊物，由民生实业股份有限公司刊行，周刊，每周一出版，1926年5月10日创刊，出版地不详，1939年1月因重庆遭日军轰炸，印刷困难，遂暂时停刊，后于1943年6月14日复刊，[编者：《民生公司简讯复刊启示》，《简讯》1943年6月第696期，第1版。]。馆藏1943年第696、697、701、703、707、707、709、714、716、718期，1945年第789期。该刊第708期之前，一般每期2版，有中缝，自708期起采用双面排印，每期3到5版，取消中缝。内容方面，该刊由公告、通讯、专载、人事、简言、各部消息六个栏目组成。“公告”栏目，主要公布公司的重要事件及重要通函。重要事件的公告，如对“长航轮船各级仓位乘客伙食，自六月十日起，一律由乘客自理，由轮船公司代办”一事的公告。重要通函，则为公司内部的重要文件，如总协28号，对“主任副主任以上人员午后办公延长半小时至五点三十分止”的规定。“各部消息”，主要刊登公司各方面的新闻，如“卢总经理复职”，“民权轮因嘉陵江水暴涨受损”，“上游轮船运煤新规定”，“周会纪略”等消息。“专载”，主要刊载重要人物的讲话及运输方面的论著，刊中载有蒋委员对民族公司迁厂努力及运输艰辛的赞美之词，还有卢总经理载周会上的讲话。著作方面，刊中连载有译自Sidney Howard的《我们的商船队》的《海难与施救》。“人事”栏目，主要发布公司的人事变动信息，如“聘王企光先生为本公司运务处经理”、“民本轮代经理张慰松调民万轮经理”等信息。“简言”，主要刊登各方面的言论，如《卢总经理复职，同仁应有的认识》、《为什么要禁赌》、《人定胜天》等。此外，第708期之前的中缝部分，主要担负刊登公司启示的功能，载有《民生公司简讯复刊启示》、《本公司整理人事登记启示》等内容。《简讯》，是民生实业股份有限公司出版的一份企业刊物，该刊是民生公司管理公司内部事务的重要工具，也是外界了解公司