INSERT INTO `ea_guji_books` VALUES ('1494', '05fed51451b0f08dd6c672257dd6876a', '11', '268', '268', '527', '新女性', '新女性', '中华文化出版公司出版', '[\"D442.64\"]', '1944', 'C:\\Users\\Administrator\\Desktop\\hello_word\\sources', '', '', '1944年10月在上海创刊，由届晁、李天理编辑，中华文化出版公司出版。月刊，属于女性综合类刊物。该刊不分栏目，其撰稿人有郑建廷、届晁、冯蘅、苏青、沈凤威、丁蕙英、俞智英、袁鹰等。该刊旨在唤起和增强女性社会责任意识。内容较丰富，有讨论关于女性人格、知识、健康生活方面的问题，如《怎样完成新女性的责任》、《禁止缠足始末记》、《关于女性的消费流水账》、《十九世纪的中国女性美容术（附图）》、《中国的蜜月风俗》等；载有少量妇女政治活动史料，如《清末民初的妇女政治活动》、《太行山脉历险记：共匪魔掌下的女政治员生活记录》；也刊有小说、诗歌、智慧测验等小文章，如《中国最古的一部小说穆天子传》、《汉代妇女诗歌掇拾：虞美人》、《吉泼赛的歌者：小人物的悲哀之一》等。曾刊登过法国著名雕刻家婆尔汀的作品与战时体制下的日本新女性图片。该刊为研究四十年代的女性提供了一定的资料。', '0', '191', '1', '', '[\"新女性\"]', '[\"A0117\"]', 'X', '中华文化出版公司出版', '月刊', '届晁,李天理编辑', '上海', '10416/4911', '1944年10月在上海创刊，由届晁、李天理编辑，中华文化出版公司出版。月刊，属于女性综合类刊物。该刊不分栏目，其撰稿人有郑建廷、届晁、冯蘅、苏青、沈凤威、丁蕙英、俞智英、袁鹰等。该刊旨在唤起和增强女性社会责任意识。内容较丰富，有讨论关于女性人格、知识、健康生活方面的问题，如《怎样完成新女性的责任》、《禁止缠足始末记》、《关于女性的消费流水账》、《十九世纪的中国女性美容术（附图）》、《中国的蜜月风俗》等；载有少量妇女政治活动史料，如《清末民初的妇女政治活动》、《太行山脉历险记：共匪魔掌下的女政治员生活记录》；也刊有小说、诗歌、智慧测验等小文章，如《中国最古的一部小说穆天子传》、《汉代妇女诗歌掇拾：虞美人》、《吉泼赛的歌者：小人物的悲哀之一》等。曾刊登过法国著名雕刻家婆尔汀的作品与战时体制下的日本新女性图片。该刊为研究四十年代的女性提供了一定的资料。', null, null, null);
