INSERT INTO `ea_guji_books` VALUES ('1499', '0618942ccf7aec10cf89e65ee47937d4', '11', '268', '268', '527', '笑话杂志', '笑话杂志', '郑小平编辑;  静安出版社 [发行]', '[\"I276.8\"]', '0', 'C:\\Users\\Administrator\\Desktop\\hello_word\\sources', '', '', '1942年12月创刊于上海，收录至1943年4月第3期。郑小平编辑，静安出版社发行。月刊，属文学类刊物。主要栏目有好莱坞影星幽默故事及笑话、影星幽默故事及最新笑话、古董店。该刊旨在“以笑来代替补药”，以此杂志作为“长期贡献给读者一种最有效验的补身药品”，刊载有大量影星幽默故事与通俗笑话及故事，如《聪明的妻子》、《别字知县》、《病人和看护》等；并有专栏“文艺园地”，发表长篇剧作，刊载有《下雪的时候》等创作。该刊发行于日军统治下的上海，这样一本专载笑话的杂志对于饱受日军压迫的上海人民无疑是一种精神的慰藉，对于丰富民众生活起到了一定的作用。附：《编辑室谈话》大笑可以延年益寿，无论何人都不否认这句话，有医学常识的人，还可以告诉你大笑为什么可以延年益寿。可是在我们现实的生活中，一年三百六十五日终究可以逢到多少有趣的事，使你心花怒放，捧腹大笑呢？所以我们需要笑，却没有可笑的题材，岂非憾事！编者素来主张，以笑来代替补药，这本笑话杂志出世，就是长期贡献给读者一种最有效验的补身药品。', '0', '191', '1', '', '[\"笑话杂志\"]', '[\"A0112\"]', 'X', '郑小平编辑;  静安出版社 [发行]', '月刊', '', '上海', '上图17323/67050', '1942年12月创刊于上海，收录至1943年4月第3期。郑小平编辑，静安出版社发行。月刊，属文学类刊物。主要栏目有好莱坞影星幽默故事及笑话、影星幽默故事及最新笑话、古董店。该刊旨在“以笑来代替补药”，以此杂志作为“长期贡献给读者一种最有效验的补身药品”，刊载有大量影星幽默故事与通俗笑话及故事，如《聪明的妻子》、《别字知县》、《病人和看护》等；并有专栏“文艺园地”，发表长篇剧作，刊载有《下雪的时候》等创作。该刊发行于日军统治下的上海，这样一本专载笑话的杂志对于饱受日军压迫的上海人民无疑是一种精神的慰藉，对于丰富民众生活起到了一定的作用。附：《编辑室谈话》大笑可以延年益寿，无论何人都不否认这句话，有医学常识的人，还可以告诉你大笑为什么可以延年益寿。可是在我们现实的生活中，一年三百六十五日终究可以逢到多少有趣的事，使你心花怒放，捧腹大笑呢？所以我们需要笑，却没有可笑的题材，岂非憾事！编者素来主张，以笑来代替补药，这本笑话杂志出世，就是长期贡献给读者一种最有效验的补身药品。', null, null, null);
