INSERT INTO `ea_guji_books` VALUES ('1527', '06935815d6134f84a169a1d8ab728474', '11', '268', '268', '527', '京中期刊', '京中期刊', '京中期刊社经理部 [发行]', '[\"G639.286.5\"]', '1934', 'C:\\Users\\Administrator\\Desktop\\hello_word\\sources', '', '', '广州市私立南京中学校刊。内容除通报校闻和校务情况外，主要发表师生们的时事评论，有关社会科学的研究论文和一些文学小作品等。', '0', '191', '1', '', '[\"京中期刊\"]', '[\"A0120\",\"A0302\"]', 'J', '京中期刊社经理部 [发行]', '月刊', '京中期刊社编辑部编辑', '广州', '16656/64830', '广州市私立南京中学校刊。内容除通报校闻和校务情况外，主要发表师生们的时事评论，有关社会科学的研究论文和一些文学小作品等。', null, null, null);
INSERT INTO `ea_guji_books` VALUES ('1528', '069b8bb85e101ef65759a9529d7c317a', '11', '268', '268', '527', '江宁民众', '江宁民众', '', '[\"G725\"]', '1935', 'C:\\Users\\Administrator\\Desktop\\hello_word\\sources', '', '', '1935年4月创刊于江苏江宁，1936年1月第11期停刊，馆藏3期。由江宁民众周刊社编辑、发行。周刊，属于农民教育刊物。进珊、参百流、祥鼎、赵天喜、王金桂、刘峙、李宗黄、山农等担任撰稿人。该刊内容主要是农村建设与民族复兴，农业与教育，农事常识以及农村卫生常识等。创办的目的是为了启迪民智，加强地方建设。教育，自古就是国家建设发展的重要因素，教育的发展对于国家的进步有着至关重要的影响，当时的有识之士就已经有了“教育强国”的观念，纷纷从事于教育研究和教育事业。如进珊的《今日江宁之民众教育》一文，以当前发展的需要为出发点，说明教育的重要性和必要性，指出江宁的社会教育的任务是“拿教育做建设的过程，建设新的社会”，也就是“以唯生的认识，实施三民主义的建设训练，培养广大农民的建设力量，一致团结于党的领导之下，巩固训政自治的基础”，也反映了该刊的政治倾向和政治立场。参百流的《江宁自治实验县的乡镇小学》和祥鼎的《怎样训练儿童的思想能力》，通过对江宁地区乡镇小学的调查和研究，探讨了小学教育及儿童思维能力培养与训练的方式方法，并针对儿童的心理和生理特征，对症下药，制定适当的小学教育计划，以促进儿童全面的健康成长。此外，《第四学区运动会速写》一文，刊登了与文章相关的照片和图片，真实、直观、生动反映了运动员们的精神状态以及运动会的热闹场景。该刊所刊登的文章，主要是对江宁地区社会、文化、教育、习俗等的介绍，方便读者对该区域的认识和了解，也有利于政府当局政策的制定和推行。', '0', '191', '1', '', '[\"江宁民众\"]', '[\"A0119\"]', 'J', '', '周刊', '江宁民众周刊社编辑 [并发行]', '江宁(江苏)', '03775/14241', '1935年4月创刊于江苏江宁，1936年1月第11期停刊，馆藏3期。由江宁民众周刊社编辑、发行。周刊，属于农民教育刊物。进珊、参百流、祥鼎、赵天喜、王金桂、刘峙、李宗黄、山农等担任撰稿人。该刊内容主要是农村建设与民族复兴，农业与教育，农事常识以及农村卫生常识等。创办的目的是为了启迪民智，加强地方建设。教育，自古就是国家建设发展的重要因素，教育的发展对于国家的进步有着至关重要的影响，当时的有识之士就已经有了“教育强国”的观念，纷纷从事于教育研究和教育事业。如进珊的《今日江宁之民众教育》一文，以当前发展的需要为出发点，说明教育的重要性和必要性，指出江宁的社会教育的任务是“拿教育做建设的过程，建设新的社会”，也就是“以唯生的认识，实施三民主义的建设训练，培养广大农民的建设力量，一致团结于党的领导之下，巩固训政自治的基础”，也反映了该刊的政治倾向和政治立场。参百流的《江宁自治实验县的乡镇小学》和祥鼎的《怎样训练儿童的思想能力》，通过对江宁地区乡镇小学的调查和研究，探讨了小学教育及儿童思维能力培养与训练的方式方法，并针对儿童的心理和生理特征，对症下药，制定适当的小学教育计划，以促进儿童全面的健康成长。此外，《第四学区运动会速写》一文，刊登了与文章相关的照片和图片，真实、直观、生动反映了运动员们的精神状态以及运动会的热闹场景。该刊所刊登的文章，主要是对江宁地区社会、文化、教育、习俗等的介绍，方便读者对该区域的认识和了解，也有利于政府当局政策的制定和推行。', null, null, null);
