INSERT INTO `ea_guji_books` VALUES ('1550', '06e287c2bff722db5ce3590c3e4fb85c', '11', '268', '268', '527', '争存', '争存', '争存半月刊社 [发行]', '[\"D693.09\"]', '1936', 'C:\\Users\\Administrator\\Desktop\\hello_word\\sources', '', '', '经济类刊物。创刊目的是为了研究民族生存的途径，和怎样才能争取民族解放的胜利。内容有论述中国农村经济的崩溃与农村复兴的方法，指出水利事业需要统一管理。', '0', '191', '1', '', '[\"争存\"]', '[\"A0120\"]', 'Z', '争存半月刊社 [发行]', '半月刊', '邹孟编辑', '上海', '06105/21873', '经济类刊物。创刊目的是为了研究民族生存的途径，和怎样才能争取民族解放的胜利。内容有论述中国农村经济的崩溃与农村复兴的方法，指出水利事业需要统一管理。', null, null, null);
INSERT INTO `ea_guji_books` VALUES ('1551', '06ece704baa80bbc33cdcc025eee3fd8', '11', '268', '268', '527', '战时大学', '战时大学', '', '[\"K265\"]', '1937', 'C:\\Users\\Administrator\\Desktop\\hello_word\\sources', '', '', '1937年10月30日在上海创刊，1937年11月6日停刊，共发行2期。战时大学周刊社编辑部编辑、发行。周刊，属于政论性刊物。主要撰稿人有毛泽东、李宗仁、孔祥熙、宋庆龄、俞鸿钧、郭沬若、马君武、田汉、宋子文、杨虎城、朱德、廖承志、章乃器等。主要栏目有社评、特稿、青年运动之部、青年政治之部等栏目。该刊发表与抗日战争形势有关的各界知名人士的文章、漫说、木刻、诗文，所载文章大都为政界、文艺界知名人士所作。该刊重要文章有毛泽东的《国共两党的统一战线》、蒋介石的《抗战的立场》、杨虎城的《中国的对日抗战》、宋子文的《中国抗战越久，地位也就越强》等；文学方面有郭沫若、田汉、叶恭绰的诗等作品。另外，该刊还载有宋庆龄的《你的生命并不是你个人的：一封敦促鲁迅先生就医的信》、丁玲的《文艺在西北新区》、章乃器的《胜利的退却》与《紧紧的把握住矛盾》等文；并刊登大量译自国外的文章，有马柯西谟夫的作品《论蒋介石将军的三次演说》、陈在岗翻译的克尔兰的文章《远东的新形势：英国工党领袖关于中国印象的广播演说》、戴望舒翻译的古久列的《国际点名录：为中国的觉醒而欢呼》等。该刊是战争时期所创的探讨战争形势、鼓舞人民斗争的政论刊物，是我们研究我国战时政策的参照之一。', '0', '191', '1', '', '[\"战时大学\"]', '[\"A0124\"]', 'Z', '', '周刊', '战时大学周刊社编辑部编辑 [并发行]', '上海', '12976/45806_07', '1937年10月30日在上海创刊，1937年11月6日停刊，共发行2期。战时大学周刊社编辑部编辑、发行。周刊，属于政论性刊物。主要撰稿人有毛泽东、李宗仁、孔祥熙、宋庆龄、俞鸿钧、郭沬若、马君武、田汉、宋子文、杨虎城、朱德、廖承志、章乃器等。主要栏目有社评、特稿、青年运动之部、青年政治之部等栏目。该刊发表与抗日战争形势有关的各界知名人士的文章、漫说、木刻、诗文，所载文章大都为政界、文艺界知名人士所作。该刊重要文章有毛泽东的《国共两党的统一战线》、蒋介石的《抗战的立场》、杨虎城的《中国的对日抗战》、宋子文的《中国抗战越久，地位也就越强》等；文学方面有郭沫若、田汉、叶恭绰的诗等作品。另外，该刊还载有宋庆龄的《你的生命并不是你个人的：一封敦促鲁迅先生就医的信》、丁玲的《文艺在西北新区》、章乃器的《胜利的退却》与《紧紧的把握住矛盾》等文；并刊登大量译自国外的文章，有马柯西谟夫的作品《论蒋介石将军的三次演说》、陈在岗翻译的克尔兰的文章《远东的新形势：英国工党领袖关于中国印象的广播演说》、戴望舒翻译的古久列的《国际点名录：为中国的觉醒而欢呼》等。该刊是战争时期所创的探讨战争形势、鼓舞人民斗争的政论刊物，是我们研究我国战时政策的参照之一。', null, null, null);
