INSERT INTO `ea_guji_books` VALUES ('1563', '072a7c91d36cbf511da2ecc6aba119ec', '11', '268', '268', '527', '浙江省立农业改良场年报', '浙江省立农业改良场年报', '', '[\"S339\"]', '1932', 'C:\\Users\\Administrator\\Desktop\\hello_word\\sources', '', '', '农业刊物。本刊将一年的工作概况，汇编成册，藉作整个之检阅，以为将来之龟鉴。刊内载文有关全省的改良育种并实施园艺调查、肥料试验、气象观测，以及指导推广等。刊内载有本场沿革、组织系统、职员名录、章则与规程、大事记、摄影照片等资料。', '0', '191', '1', '', '[\"浙江省立农业改良场年报\"]', '[\"A0120\"]', 'Z', '', '年刊', '浙江省立农业改良场年报编辑部编辑 [并发行]', '杭州', '07534/27383', '农业刊物。本刊将一年的工作概况，汇编成册，藉作整个之检阅，以为将来之龟鉴。刊内载文有关全省的改良育种并实施园艺调查、肥料试验、气象观测，以及指导推广等。刊内载有本场沿革、组织系统、职员名录、章则与规程、大事记、摄影照片等资料。', null, null, null);
INSERT INTO `ea_guji_books` VALUES ('1564', '072da36d8fd2d6b4adcc924cb1a013e4', '11', '268', '268', '527', '江苏(省立)苏州农业学校学生自治会会刊', '江苏(省立)苏州农业学校学生自治会会刊', '', '[\"G718.4\"]', '1934', 'C:\\Users\\Administrator\\Desktop\\hello_word\\sources', '', '', '1934年7月在江苏苏州创刊，仅见民国23年7月的1期。由江苏省立苏州农业学校学生自治会编辑、发行。逐页题名《苏农学生自治会会刊》。刊期不详，属于学生自治会会刊。该刊谢景先、王春沐、景元、王庆云、许寿大、潘永贵、翟兆基等人为主要撰稿人，内容包括论著、研究、演讲、文艺、工作报告等板块。该刊刊载的文章有关于农业方面的论述、有文学作品、还有本校学生自治会工作报告及其他类文章，另外还刊有附录。苏州丝绸天下闻名，其养蚕业备受关注，该刊创办于苏州，自然对蚕丝业相关内容的介绍花了大量笔墨，如谢景先的《近年我国蚕丝业惨败之原因及其挽救法》，主要分析了近年我国蚕丝业的发展现状、造成惨败的原因以及扭转蚕丝业颓败市场的办法，给蚕丝业的发展提供了借鉴和启迪；《养蚕指导》取自张諟名、沙祖耀记录的本校1934级学生在无锡实习听讲的笔记，介绍了养蚕的方法、饲养步骤以及需要注意的问题等，对养蚕具有一定的指导作用。农业方面的还有景元的《菜类猿叶虫的防治法》、景先的《大飞蝗之生活史及其防治法》等对病虫害防治方面的文章。该刊作为本校的学生自治会会刊，也刊载了很多学生活动类的文章，如王春沐的《新生活运动与青年》，获得演说竞赛的一等奖；王庆云的《暮春素描》，获文艺竞赛的一等奖，等等。此外，还登载了本校学生自治会的工作报告，以方便读者对本校事务、学生活动以及整体学生风貌有大致的了解。该刊不仅仅是本校学生自治会的会刊，其收录的文章在一定程度上也是对当时社会情况的真实写照，如王春沐的《新生活运动与青年》，就反应了当时蒋介石推行的新生活运动在学生中的反响，为研究当时的苏州地区提供了少许资料。', '0', '191', '1', '', '[\"江苏(省立)苏州农业学校学生自治会会刊\"]', '[\"A0119\"]', 'J', '', '刊期不详', '江苏省立苏州农业学校学生自治会编辑 [并发行]', '苏州', '03859/14570', '1934年7月在江苏苏州创刊，仅见民国23年7月的1期。由江苏省立苏州农业学校学生自治会编辑、发行。逐页题名《苏农学生自治会会刊》。刊期不详，属于学生自治会会刊。该刊谢景先、王春沐、景元、王庆云、许寿大、潘永贵、翟兆基等人为主要撰稿人，内容包括论著、研究、演讲、文艺、工作报告等板块。该刊刊载的文章有关于农业方面的论述、有文学作品、还有本校学生自治会工作报告及其他类文章，另外还刊有附录。苏州丝绸天下闻名，其养蚕业备受关注，该刊创办于苏州，自然对蚕丝业相关内容的介绍花了大量笔墨，如谢景先的《近年我国蚕丝业惨败之原因及其挽救法》，主要分析了近年我国蚕丝业的发展现状、造成惨败的原因以及扭转蚕丝业颓败市场的办法，给蚕丝业的发展提供了借鉴和启迪；《养蚕指导》取自张諟名、沙祖耀记录的本校1934级学生在无锡实习听讲的笔记，介绍了养蚕的方法、饲养步骤以及需要注意的问题等，对养蚕具有一定的指导作用。农业方面的还有景元的《菜类猿叶虫的防治法》、景先的《大飞蝗之生活史及其防治法》等对病虫害防治方面的文章。该刊作为本校的学生自治会会刊，也刊载了很多学生活动类的文章，如王春沐的《新生活运动与青年》，获得演说竞赛的一等奖；王庆云的《暮春素描》，获文艺竞赛的一等奖，等等。此外，还登载了本校学生自治会的工作报告，以方便读者对本校事务、学生活动以及整体学生风貌有大致的了解。该刊不仅仅是本校学生自治会的会刊，其收录的文章在一定程度上也是对当时社会情况的真实写照，如王春沐的《新生活运动与青年》，就反应了当时蒋介石推行的新生活运动在学生中的反响，为研究当时的苏州地区提供了少许资料。', null, null, null);
