INSERT INTO `ea_guji_books` VALUES ('1566', '073b7af6432461d83616ccd8df65803c', '11', '268', '268', '527', '当代诗刊', '当代诗刊', '', '[\"I226\"]', '1935', 'C:\\Users\\Administrator\\Desktop\\hello_word\\sources', '', '', '1935年1月1日在上海创刊，1935年5月停刊，共发行4期。该刊由当代诗刊社编辑，出版，月刊，属于文学刊物。主要撰稿人有朱维基、蒋有林、素农、徐国馨、郑庚伯、曼茵、茱英、赵景深、郭伯恭、陈雨门、周白鸿、邵冠华、赵颐年、臧克家、吕绍光、章石承、陈守梅、罗伽、锡金等。主要栏目不详。该刊以介绍新诗作品与国外优秀译作为主。刊有臧克家的《夜》、朱维基的《爱的历程》、赵景深的译作《火里的军队》等文章。载有诗歌《爱的历程》、《为了她》、《爱的哲学》等有关爱的诗歌；也载有《北国之春》、《古城三月》、《月夜池畔》、《古渡》、《雨夜》、《马来亚之忆》等写景抒情的诗歌；除此之外，该刊还载有《饥寒的人们》、《火里的军队》、《灾象》、《沦落》、《贫穷的支那》、《陶工的歌》、《抢米》、《长途上》等诗歌，来描述当时国内战争下人们的生活。该刊是重要的文学刊物，许多著名作家都为该刊撰稿，丰富了民国时期的文学宝库，具有浓烈的文学气息。', '0', '191', '1', '', '[\"当代诗刊\"]', '[\"A0117\"]', 'D', '', '月刊', '当代诗刊社编辑 [并出版]', '上海', '11333/39700', '1935年1月1日在上海创刊，1935年5月停刊，共发行4期。该刊由当代诗刊社编辑，出版，月刊，属于文学刊物。主要撰稿人有朱维基、蒋有林、素农、徐国馨、郑庚伯、曼茵、茱英、赵景深、郭伯恭、陈雨门、周白鸿、邵冠华、赵颐年、臧克家、吕绍光、章石承、陈守梅、罗伽、锡金等。主要栏目不详。该刊以介绍新诗作品与国外优秀译作为主。刊有臧克家的《夜》、朱维基的《爱的历程》、赵景深的译作《火里的军队》等文章。载有诗歌《爱的历程》、《为了她》、《爱的哲学》等有关爱的诗歌；也载有《北国之春》、《古城三月》、《月夜池畔》、《古渡》、《雨夜》、《马来亚之忆》等写景抒情的诗歌；除此之外，该刊还载有《饥寒的人们》、《火里的军队》、《灾象》、《沦落》、《贫穷的支那》、《陶工的歌》、《抢米》、《长途上》等诗歌，来描述当时国内战争下人们的生活。该刊是重要的文学刊物，许多著名作家都为该刊撰稿，丰富了民国时期的文学宝库，具有浓烈的文学气息。', null, null, null);
