INSERT INTO `ea_guji_books` VALUES ('1585', '07a610e267a5e0ded8a3824c2088888d', '11', '268', '268', '527', '江苏财政汇报', '江苏财政汇报', '江苏省政府财政厅公报处   [并发行]', '', '0', 'C:\\Users\\Administrator\\Desktop\\hello_word\\sources', '', '', '20年代地方刊物。刊载国民党政府各项命令、江苏省所有财政法规、社察报告、调查表册以及各项财会收支统计。', '0', '191', '1', '', '[\"江苏财政汇报\"]', '[\"A0102\"]', 'J', '江苏省政府财政厅公报处   [并发行]', '月刊', '', '江苏', '上图14360/052168-75', '20年代地方刊物。刊载国民党政府各项命令、江苏省所有财政法规、社察报告、调查表册以及各项财会收支统计。', null, null, null);
