INSERT INTO `ea_guji_books` VALUES ('1614', '081dece28e45191e943213a69ccaf25e', '11', '268', '268', '527', '文撷半月刊', '文撷半月刊', '文撷出版社 [并发行]', '', '0', 'C:\\Users\\Administrator\\Desktop\\hello_word\\sources', '', '', '30年代时事刊物。该刊主要围绕抗日选择文章。其内容多为对日本侵略中国以及汉奸汪精卫卖国投敌进行揭露和清算的论述。该刊同时对世界动乱的形势进行分析，指出中国一定会抗战胜利。', '0', '191', '1', '', '[\"文撷半月刊\"]', '[\"A0102\"]', 'W', '文撷出版社 [并发行]', '半月刊', '', '上海', '上图(1324)672; 1324/5206', '30年代时事刊物。该刊主要围绕抗日选择文章。其内容多为对日本侵略中国以及汉奸汪精卫卖国投敌进行揭露和清算的论述。该刊同时对世界动乱的形势进行分析，指出中国一定会抗战胜利。', null, null, null);
