INSERT INTO `ea_guji_books` VALUES ('1650', '08b87b32cb97bc0bfa25d56d6e1dfbad', '11', '268', '268', '527', '八路军军政杂志', '八路军军政杂志', '国民革命军第八路军政治部 [出版]', '[\"E297.3\"]', '1939', 'C:\\Users\\Administrator\\Desktop\\hello_word\\sources', '', '', '军政刊物。第七期载有“中国共产党中央委员会为抗战两周年纪念对时局宣言”，刊有贺龙的“一二零师抗战两年来的总结”，陈毅的“江南游击区域工作经验片谈”，“晋察冀军区部队最近政治工作概况”，王首道的“八路军中国共产党的工作”，八路军新四军捷讯汇报，蒋委员长告全国军民书等，及有关军事的译丛，通讯、木刻。', '0', '191', '1', '', '[\"八路军军政杂志\"]', '[\"A0124\"]', 'B', '国民革命军第八路军政治部 [出版]', '月刊', '国民革命军第八路军军政杂志社编辑', '延安', 'GWJ0052/0052', '军政刊物。第七期载有“中国共产党中央委员会为抗战两周年纪念对时局宣言”，刊有贺龙的“一二零师抗战两年来的总结”，陈毅的“江南游击区域工作经验片谈”，“晋察冀军区部队最近政治工作概况”，王首道的“八路军中国共产党的工作”，八路军新四军捷讯汇报，蒋委员长告全国军民书等，及有关军事的译丛，通讯、木刻。', null, null, null);
INSERT INTO `ea_guji_books` VALUES ('1651', '08c1620fc67fbc679b6fceda31242613', '11', '268', '268', '527', '南开思潮', '南开思潮', '南开思潮报社经理部 [发行]', '[\"C55\"]', '1917', 'C:\\Users\\Administrator\\Desktop\\hello_word\\sources', '', '', '学生刊物。内容以小说、散文、诗歌、杂谈，抒情性文艺作品居多。并刊登关于政治、教育、化理道德等方面的言论和学术论文以及科学常识。此外还附有该校教职员照片、校舍以及反映教学、生活的摄影作品。', '0', '191', '1', '', '[\"南开思潮\"]', '[\"A0120\"]', 'N', '南开思潮报社经理部 [发行]', '半年刊', '南开思潮报社编辑部编辑', '天津', '06933/24566', '学生刊物。内容以小说、散文、诗歌、杂谈，抒情性文艺作品居多。并刊登关于政治、教育、化理道德等方面的言论和学术论文以及科学常识。此外还附有该校教职员照片、校舍以及反映教学、生活的摄影作品。', null, null, null);
INSERT INTO `ea_guji_books` VALUES ('1652', '08d2506dc26a36ffde5eedbb2ef67cb7', '11', '268', '268', '527', '新命：命学苑苑刊', '新命：命学苑苑刊', '命学苑 [发行]', '[\"B992.3\"]', '1927', 'C:\\Users\\Administrator\\Desktop\\hello_word\\sources', '', '', '《新命》，1927年10月于上海出版，编辑为命学苑编纂处，发行者为上海命学苑，竖排版。刊期不详。为上海命学苑苑刊，命相刊物。\n命学苑为民间组织，据第一期所载启事载，该社成立“本研究五行高深命理之宗旨组织而成，系学会讨论性质。”该刊以易经五行为基础，阐述广义上的命相现象，按首期《编者言》所述，宗旨是“用革命的手段，实事求是的精神，科哲学式的方法，来研究，整理，和改革发明”，创立宣言为“广为唯物之义，体之于行，验证之以事，类举科学之精神，阐五行之真理，方将发扬光大，以成革故鼎新之学。”试图打破原有“命”学神秘与迷信的色彩，以科学的方法研究整理，以期革新命学。刊物中载有康有为、郑孝胥等人为该刊所作题词。\n主要撰稿人有华胥大夫、叶德辉等。该刊载文包括几大板块：命学原理、名造评案、命学新案，如《吴佩孚命造评》《正偏财皆为外遇说》、《父母年命与命造之关系》等，仍以五行八卦断命，并未脱离中国古代的命学范畴。刊物内也搜集不少与命学研究有关的文献，如风水创始人唐代李虚中所著《李虚中命书》以及有关资料《钦定四库全书提要》、《韩昌黎撰李虛中墓志铭》等。\n另外有大量名造类编集，文苑等内容，如《佛日楼诗納海集：戈登路大华园舞场啜茗》、《临江仙?生日写怀》等，都是旧体诗词，多为刊物编者所作，内容以状物叙事为主，与刊物主旨关联不大。《名造类編：前集：甲編：帝王、后妃类》以天干为序记录明清两代帝王后妃传略，作为命理研究参考资料。刊物中还附有命学苑主要创始人程霖生、李饮光、洪震修等人的介绍及照片。该刊对读者了解当时的命理风水之学有一定的参考作用。', '0', '191', '1', '', '[\"新命：命学苑苑刊\"]', '[\"A0117\"]', 'X', '命学苑 [发行]', '刊期不详', '命学苑编纂处编辑', '上海', '10668/5038', '《新命》，1927年10月于上海出版，编辑为命学苑编纂处，发行者为上海命学苑，竖排版。刊期不详。为上海命学苑苑刊，命相刊物。\n命学苑为民间组织，据第一期所载启事载，该社成立“本研究五行高深命理之宗旨组织而成，系学会讨论性质。”该刊以易经五行为基础，阐述广义上的命相现象，按首期《编者言》所述，宗旨是“用革命的手段，实事求是的精神，科哲学式的方法，来研究，整理，和改革发明”，创立宣言为“广为唯物之义，体之于行，验证之以事，类举科学之精神，阐五行之真理，方将发扬光大，以成革故鼎新之学。”试图打破原有“命”学神秘与迷信的色彩，以科学的方法研究整理，以期革新命学。刊物中载有康有为、郑孝胥等人为该刊所作题词。\n主要撰稿人有华胥大夫、叶德辉等。该刊载文包括几大板块：命学原理、名造评案、命学新案，如《吴佩孚命造评》《正偏财皆为外遇说》、《父母年命与命造之关系》等，仍以五行八卦断命，并未脱离中国古代的命学范畴。刊物内也搜集不少与命学研究有关的文献，如风水创始人唐代李虚中所著《李虚中命书》以及有关资料《钦定四库全书提要》、《韩昌黎撰李虛中墓志铭》等。\n另外有大量名造类编集，文苑等内容，如《佛日楼诗納海集：戈登路大华园舞场啜茗》、《临江仙?生日写怀》等，都是旧体诗词，多为刊物编者所作，内容以状物叙事为主，与刊物主旨关联不大。《名造类編：前集：甲編：帝王、后妃类》以天干为序记录明清两代帝王后妃传略，作为命理研究参考资料。刊物中还附有命学苑主要创始人程霖生、李饮光、洪震修等人的介绍及照片。该刊对读者了解当时的命理风水之学有一定的参考作用。', null, null, null);
