INSERT INTO `ea_guji_books` VALUES ('1657', '08f6d298b3bd51f8cf6a87f2d494f265', '11', '268', '268', '527', '复兴之路', '复兴之路', '', '[\"D693.09\"]', '1935', 'C:\\Users\\Administrator\\Desktop\\hello_word\\sources', '', '', '政治刊物。刊有反共言论，自称阐扬三民主义的革命理论，肃清一切反革命的理论并揭露其内部秘密，介绍和讨论国内外政治问题，提倡精神修养，复兴中华民族战斗意识。载文有“目前政治形势与第二期国民革命”、“第二期革命与肃反”、“东非的炸弹：意阿事件”、“东欧火药库：米美尔问题”、“怎样确定自己革命的人生观”等。<收音期刊>合并于<复兴之路>。', '0', '191', '1', '', '[\"复兴之路\"]', '[\"A0126\"]', 'F', '', '刊期不详', '复兴之路社编辑 [并发行]', '南京', 'GWJ1038/1038', '政治刊物。刊有反共言论，自称阐扬三民主义的革命理论，肃清一切反革命的理论并揭露其内部秘密，介绍和讨论国内外政治问题，提倡精神修养，复兴中华民族战斗意识。载文有“目前政治形势与第二期国民革命”、“第二期革命与肃反”、“东非的炸弹：意阿事件”、“东欧火药库：米美尔问题”、“怎样确定自己革命的人生观”等。<收音期刊>合并于<复兴之路>。', null, null, null);
INSERT INTO `ea_guji_books` VALUES ('1658', '08f82b4f910995f88ce7fd4e12f1e8b3', '11', '268', '268', '527', '朝花', '朝花', '上海合记教育用品社编辑 [并发行]', '[\"I216\"]', '0', 'C:\\Users\\Administrator\\Desktop\\hello_word\\sources', '', '', '1928年12月6日在上海创刊，1929年5月16日停刊，共出20期；1929年6月1日改为《朝花旬刊》，1929年9月停刊，共出12期。由朝花社主办，上海合记教育用品社编辑、发行。旬刊，属于文学刊物。主要撰稿人有柔石、式微、梅川、昌标、真吾、樵子、裘柱常、卓治、鲁迅等，主要栏目不详。该刊以扶植刚劲质朴的文艺为宗旨，主要翻译介绍外国文艺作品，尤其是北欧、东欧的文学和版画，对推动我国新兴木刻艺术的发展起了重要作用。发表的主要作品有丹麦童话和随笔杂谈等，另附出画刊《艺苑朝花》5辑。鲁迅在该刊发表了《一九二八年世界文艺界概观》、《面包店时代》、《近代木刻选集小引》、《哈谟生的几句话》等。柔石也在该刊发表了不少小说、诗歌和杂记，如《死猫》、《狗的自杀问题》、《夜底怪眼》、《夜半的孤零的心》等。该刊刊登的翻译作品有，梅川翻译哈谟生的《生命之呼声》、采石翻译法朗士的《三诗人》、适夷翻译的爱罗先珂的《催眠歌》、岩野翻译的匈牙利摩尔那的《银柄》等文。除此之外，该刊还载有《飞沫》、《希望》等画图与木刻作品《捞网》、《老屋》、《落叶》、《休息》等。该刊对民国文学界产生了一定的影响，具有极强的文学性。', '0', '191', '1', '', '[\"朝花\"]', '[\"A0112\"]', 'C', '上海合记教育用品社编辑 [并发行]', '周刊', '', '上海', '上图(9769)4639', '1928年12月6日在上海创刊，1929年5月16日停刊，共出20期；1929年6月1日改为《朝花旬刊》，1929年9月停刊，共出12期。由朝花社主办，上海合记教育用品社编辑、发行。旬刊，属于文学刊物。主要撰稿人有柔石、式微、梅川、昌标、真吾、樵子、裘柱常、卓治、鲁迅等，主要栏目不详。该刊以扶植刚劲质朴的文艺为宗旨，主要翻译介绍外国文艺作品，尤其是北欧、东欧的文学和版画，对推动我国新兴木刻艺术的发展起了重要作用。发表的主要作品有丹麦童话和随笔杂谈等，另附出画刊《艺苑朝花》5辑。鲁迅在该刊发表了《一九二八年世界文艺界概观》、《面包店时代》、《近代木刻选集小引》、《哈谟生的几句话》等。柔石也在该刊发表了不少小说、诗歌和杂记，如《死猫》、《狗的自杀问题》、《夜底怪眼》、《夜半的孤零的心》等。该刊刊登的翻译作品有，梅川翻译哈谟生的《生命之呼声》、采石翻译法朗士的《三诗人》、适夷翻译的爱罗先珂的《催眠歌》、岩野翻译的匈牙利摩尔那的《银柄》等文。除此之外，该刊还载有《飞沫》、《希望》等画图与木刻作品《捞网》、《老屋》、《落叶》、《休息》等。该刊对民国文学界产生了一定的影响，具有极强的文学性。', null, null, null);
