INSERT INTO `ea_guji_books` VALUES ('1748', '0a78d0126b50e985b64cd04db3e4a9c7', '11', '268', '268', '527', '国立中央工校铜梁学友会会刊', '国立中央工校铜梁学友会会刊', '', '[\"G649.28\"]', '1943', 'C:\\Users\\Administrator\\Desktop\\hello_word\\sources', '', '', '工业学校学友会刊物。本刊旨在使家乡人士明白工业现状、国家所需及学会会员的生活片段。载文有“从电力谈到农业”、“怎样研究科学”、“工业教育与国家的关系”等。', '0', '191', '1', '', '[\"国立中央工校铜梁学友会会刊\"]', '[\"A0126\"]', 'G', '', '[刊期不详]', '国立中央工校铜梁学友会编辑 [并发行]', '沙坪坝(重庆)', 'CQL0415/0415', '工业学校学友会刊物。本刊旨在使家乡人士明白工业现状、国家所需及学会会员的生活片段。载文有“从电力谈到农业”、“怎样研究科学”、“工业教育与国家的关系”等。', null, null, null);
INSERT INTO `ea_guji_books` VALUES ('1749', '0a79ba9519b07e7b1f2cce17dbca2d48', '11', '268', '268', '527', '现代中国(上海1939)', '现代中国(上海1939)', '现代中国周刊社编辑;  哈华特 [发行人]', '[\"D693\"]', '0', 'C:\\Users\\Administrator\\Desktop\\hello_word\\sources', '', '', '1939年4月创刊于上海孤岛，收录至10月第3卷第25期；该刊自创刊便一直受到日本侵略者压迫，1940年8月被迫迁至江苏溧阳，复刊第2卷第1期，未及一年又遭日寇破坏，于1941年3月第3卷第2期后被迫再次停刊；1942年11月12日，该刊于皖南再次复刊第3卷第1期，收录至12月第2期。今上海图书馆馆藏共18期。该刊先后由现代中国周刊社、现代中国杂志社、江苏省江南文化事业委员会、现代中国杂志编辑委员会等编辑；该刊在沪时发行人为美国人哈华特，出版方初为美商好华出版公司，后为美商现代中国周刊社，该刊由五洲书报社等经售；该刊在苏时，出版方初为现代中国杂志社，后为江苏省江南文化事业委员会，总经售为宜兴永宁商店，印刷则先后由宜兴商务印刷局、江南新新印刷馆等负责；该刊在皖时，发行人为张文伯，出版方先后为现代中国杂志社、江南文化事业委员会，总经售为中国文化服务社安徽分社，印刷由屯溪印刷生产合作社负责。初为周刊，后改为月刊。该刊属于综合性时政刊物。主要撰稿人有周绍成、方志农、林亦英、胡养蒙、冷容庵、胡秋原等。该刊主要栏目有十日谈、社论、特辑、微言、特?写、战地漫笔、编辑室、译文等。该刊主要内容为分析抗战时期中国及世界政治局势，就战时政治、经济、文化、社会等问题发表评论，指导民众确立持久抗战的信念，刊载一周国内外时事政治。该刊作为抗日战争时期创办于上海孤岛的时政刊物，带有鲜明的反日立场，所刊载多为揭露日本侵略者罪证、呼吁民众抗战救国的文章。如冷容庵撰写的《以扑灭伪组织纪念总理忌辰》一文，揭露了汪伪卖国投敌的行径，呼吁江南军民扑灭在江南游击区内出现的伪组织。此外该刊也关注国际政治局势的变化，如《泰倭“友好”》一文，指出泰国虽有日本签订所谓“友好”条约，也难逃被日寇吞并之命运。', '0', '191', '1', '', '[\"现代中国(上海1939)\",\"Modern China Weekly Review\"]', '[\"A0112\"]', 'X', '现代中国周刊社编辑;  哈华特 [发行人]', '周刊,月刊', '', '上海,屯溪', '上图(8471)4031; 8471/31255-56', '1939年4月创刊于上海孤岛，收录至10月第3卷第25期；该刊自创刊便一直受到日本侵略者压迫，1940年8月被迫迁至江苏溧阳，复刊第2卷第1期，未及一年又遭日寇破坏，于1941年3月第3卷第2期后被迫再次停刊；1942年11月12日，该刊于皖南再次复刊第3卷第1期，收录至12月第2期。今上海图书馆馆藏共18期。该刊先后由现代中国周刊社、现代中国杂志社、江苏省江南文化事业委员会、现代中国杂志编辑委员会等编辑；该刊在沪时发行人为美国人哈华特，出版方初为美商好华出版公司，后为美商现代中国周刊社，该刊由五洲书报社等经售；该刊在苏时，出版方初为现代中国杂志社，后为江苏省江南文化事业委员会，总经售为宜兴永宁商店，印刷则先后由宜兴商务印刷局、江南新新印刷馆等负责；该刊在皖时，发行人为张文伯，出版方先后为现代中国杂志社、江南文化事业委员会，总经售为中国文化服务社安徽分社，印刷由屯溪印刷生产合作社负责。初为周刊，后改为月刊。该刊属于综合性时政刊物。主要撰稿人有周绍成、方志农、林亦英、胡养蒙、冷容庵、胡秋原等。该刊主要栏目有十日谈、社论、特辑、微言、特?写、战地漫笔、编辑室、译文等。该刊主要内容为分析抗战时期中国及世界政治局势，就战时政治、经济、文化、社会等问题发表评论，指导民众确立持久抗战的信念，刊载一周国内外时事政治。该刊作为抗日战争时期创办于上海孤岛的时政刊物，带有鲜明的反日立场，所刊载多为揭露日本侵略者罪证、呼吁民众抗战救国的文章。如冷容庵撰写的《以扑灭伪组织纪念总理忌辰》一文，揭露了汪伪卖国投敌的行径，呼吁江南军民扑灭在江南游击区内出现的伪组织。此外该刊也关注国际政治局势的变化，如《泰倭“友好”》一文，指出泰国虽有日本签订所谓“友好”条约，也难逃被日寇吞并之命运。', null, null, null);
