INSERT INTO `ea_guji_books` VALUES ('1751', '0a82edd5de1f4598cebbe517d4caa1f1', '11', '268', '268', '527', '摄影杂志', '摄影杂志', '广州摄影工会　[发行]　', '[\"J41\",\"TB8\"]', '1922', 'C:\\Users\\Administrator\\Desktop\\hello_word\\sources', '', '', '摄影刊物。本刊以研究实用的学理技术为宗旨,登载研讨摄影艺术、冲印技术的文章,报道该工会会务,还刊有摄影作品的欣赏等。', '0', '191', '1', '', '[\"摄影杂志\",\"The monthly photo-journa\"]', '[\"A0124\",\"A0302\"]', 'S', '广州摄影工会　[发行]　', '月刊', '张雨苍主编', '广州', '13995/49008_11', '摄影刊物。本刊以研究实用的学理技术为宗旨,登载研讨摄影艺术、冲印技术的文章,报道该工会会务,还刊有摄影作品的欣赏等。', null, null, null);
INSERT INTO `ea_guji_books` VALUES ('1752', '0a8839ad5f478f44085fe5cc4273e758', '11', '268', '268', '527', '大路(上海1936)', '大路(上海1936)', '大路三日刊社 [发行]', '[\"C55\"]', '1936', 'C:\\Users\\Administrator\\Desktop\\hello_word\\sources', '', '', '社科综合性刊物，1936年10月创办于上海，三日刊，由上海大路三日刊社发行，许啸天任主干，停刊时间不详。馆藏1936年“双十节国庆纪念号”。该刊定位为青年课余读物， 其自我评价：“言论辛辣、常识丰富、小品惊俏、图画清新”。该刊主要内容为长篇小说、农村素描以及工商情报。现存纪念号的主要栏目和具体内容如下：“插图”栏目主要刊载一些战争场景和生活场景照片，如《东三省未亡以前的“安乐农家”》、《犬与猫鼠的和平》、《犬雀的和平》、《先烈的尸骨》，对于日军的暴行，该刊也加以披露，在《日海军过去在上海的行为》和《日海军最近在上海的行动》两幅图中展现日军的凶残；“国庆论文”栏目主要刊载具有爱国注意色彩的文章，如呼吁全民族奋起抗争的《起来拼一下命吧》，主张爱国教育的《普及什么教育》，还有《人民本位之大路》、《说出去都是丢脸》等；“特载”栏目主要刊登关于科学研究、抗战军事和避难知识的文章，《中国农业研究》介绍了中国农业的历史和现状，《日本海军之今昔》则介绍了日本海军由西洋式转向日本式的过程和原因，《青岛沧桑史》从《马关条约》中三国干涉日本还辽开始讲述青岛在甲午战后的命运，其中不仅讲到俄国和德国对于青岛的侵占，还涉及一战后日本强迫北洋政府签订独占青岛的“二十一条”，《都市避难法》讲述了在日军侵华中城市所受到的威胁和民众生命安全的毫无保证，继而发表了一系列有关城市避难的观点和主张，如组织遣送队、经济队、交通队、掩护队等团体来为都市民众避难保驾护航；“小品”栏目主要刊载小品文，载文有《沉沦》、《雪片》、《谈恋爱》等；“小说”栏目主要刊载一些短篇小说和诗歌，载有小说《双十节的一件礼品》、《五月》、《老四》等。该刊作为全面抗战前夕发行的刊物，其中保留了一些关于战前中国的社会状况和人们的生存状态的资料，这些资料有助于考察战前中国社会的样貌。', '0', '191', '1', '', '[\"大路(上海1936)\"]', '[\"A0130\"]', '', '', '三日刊', '许啸天主编', '上海', '00597/2584', '社科综合性刊物，1936年10月创办于上海，三日刊，由上海大路三日刊社发行，许啸天任主干，停刊时间不详。馆藏1936年“双十节国庆纪念号”。该刊定位为青年课余读物， 其自我评价：“言论辛辣、常识丰富、小品惊俏、图画清新”。该刊主要内容为长篇小说、农村素描以及工商情报。现存纪念号的主要栏目和具体内容如下：“插图”栏目主要刊载一些战争场景和生活场景照片，如《东三省未亡以前的“安乐农家”》、《犬与猫鼠的和平》、《犬雀的和平》、《先烈的尸骨》，对于日军的暴行，该刊也加以披露，在《日海军过去在上海的行为》和《日海军最近在上海的行动》两幅图中展现日军的凶残；“国庆论文”栏目主要刊载具有爱国注意色彩的文章，如呼吁全民族奋起抗争的《起来拼一下命吧》，主张爱国教育的《普及什么教育》，还有《人民本位之大路》、《说出去都是丢脸》等；“特载”栏目主要刊登关于科学研究、抗战军事和避难知识的文章，《中国农业研究》介绍了中国农业的历史和现状，《日本海军之今昔》则介绍了日本海军由西洋式转向日本式的过程和原因，《青岛沧桑史》从《马关条约》中三国干涉日本还辽开始讲述青岛在甲午战后的命运，其中不仅讲到俄国和德国对于青岛的侵占，还涉及一战后日本强迫北洋政府签订独占青岛的“二十一条”，《都市避难法》讲述了在日军侵华中城市所受到的威胁和民众生命安全的毫无保证，继而发表了一系列有关城市避难的观点和主张，如组织遣送队、经济队、交通队、掩护队等团体来为都市民众避难保驾护航；“小品”栏目主要刊载小品文，载文有《沉沦》、《雪片》、《谈恋爱》等；“小说”栏目主要刊载一些短篇小说和诗歌，载有小说《双十节的一件礼品》、《五月》、《老四》等。该刊作为全面抗战前夕发行的刊物，其中保留了一些关于战前中国的社会状况和人们的生存状态的资料，这些资料有助于考察战前中国社会的样貌。', null, null, null);
INSERT INTO `ea_guji_books` VALUES ('1753', '0a89954a918e112f946b8c8d66734455', '11', '268', '268', '527', '浙江司法半月刊', '浙江司法半月刊', '浙江高等法院编辑 [并发行]', '', '0', 'C:\\Users\\Administrator\\Desktop\\hello_word\\sources', '', '', '司法刊物。本刊以公布关于司法文件、助长司法进行、研究法律问题为宗旨。刊登内容包括国民政府、司法院及司法行政部、浙江高院与省政府公布的法规与命令，司法院之法律解释、浙江高院裁判书，关于司法改进的论著与消息以及附录等。其它刊名：《浙江司法月刊》。', '0', '191', '1', '', '[\"浙江司法半月刊\"]', '[\"A0120\",\"A0111\"]', 'Z', '浙江高等法院编辑 [并发行]', '半月刊,月刊', '', '浙江', '上图7489/27179-203', '司法刊物。本刊以公布关于司法文件、助长司法进行、研究法律问题为宗旨。刊登内容包括国民政府、司法院及司法行政部、浙江高院与省政府公布的法规与命令，司法院之法律解释、浙江高院裁判书，关于司法改进的论著与消息以及附录等。其它刊名：《浙江司法月刊》。', null, null, null);
