INSERT INTO `ea_guji_books` VALUES ('1757', '0a962e659814d60914fabaaad8f24e4c', '11', '268', '268', '527', '上海特写(上海1949)', '上海特写(上海1949)', '上海特写出版社编辑;  影艺出版公司[发行]', '', '0', 'C:\\Users\\Administrator\\Desktop\\hello_word\\sources', '', '', '时事新闻刊物。主要是一些国内、国际的政治时事新闻特写，报道当前时局动态、揭开新闻人物的秘密。', '0', '191', '1', '', '[\"上海特写(上海1949)\"]', '[\"A0102\"]', 'S', '上海特写出版社编辑;  影艺出版公司[发行]', '周刊', '', '上海', '上图(15496)392; 15496/60627', '时事新闻刊物。主要是一些国内、国际的政治时事新闻特写，报道当前时局动态、揭开新闻人物的秘密。', null, null, null);
INSERT INTO `ea_guji_books` VALUES ('1758', '0a9a7e6bb973935aff9e5153c5c00b7f', '11', '268', '268', '527', '工商月刊', '工商月刊', '', '[\"F27\"]', '1940', 'C:\\Users\\Administrator\\Desktop\\hello_word\\sources', '', '', '1940年创刊于香港，由大公出版社编辑并发行。停刊时间不详。属于工商业刊物。主要撰稿人有苏国山、杜其章、任渭访等。设有社会小说、特写、插图故事等栏目。该刊物旨在沟通工商学术，联络工商感情，提倡支持国货。载文报道当时国货厂商情况，介绍香港各行商业概况。“我们的目的布告于右：经的方面：增强国防经济壁垒，促进战时工商努力，表扬国货优质，沟通工商学术，联络工商感情；纬的方面：唤起共同努力战时生产，普遍推行国货消费”。刊发有《从爱国说到提倡国货》、《工商业预算论》、《欧战中香港金融之面面观》、《爱国龙舟歌，工商第一声》、《提倡国货急不容缓》、《从日本的危机说到提倡国货》、《从爱国说到提倡国货》等文介绍战时香港工商业的状况和受到的影响及今后发展的出路，应当立足于自力更生；《战区及香港国货准予运内地推销》、《香港铁价飞涨》、《欧战爆发英印绵纱来源锐减沪纱激增日图夺销场》、《日封锁我沿海后行业界损失七千万元》、《马来西亚市场繁荣，畅销我国货物》、《广州大闹米荒中日伪垄断居奇》等众多新闻报道反映随着抗战及二战的规模扩大对香港及其他地区工商业造成了巨大影响；《黄金白银输美统计》、《英国出入口货统计》、《去年香港各行业概况》、《二月份香港日常用品价格》、《去年香港物价指数》等文系统统计了香港及与香港有关地区的工商业发展情况的数据。该刊物对于研究抗战时期香港地区的工商业情况和对抗战的态度等方面有一定的史料价值。发刊词（苏国山）中国革命军队在抗战胜利的途径迈进，后方生产建设种子正在萌芽滋生着，国家好像身体，手足是武备，经济却是大动脉，如果血脉干枯，跟住就手足麻木，不能活动，所以中国战时生产的生长情形怎样，关系抗战胜利前途很大。优良果子的结实，自然需要我们努力去灌溉，而唤起一般的努力，断不能缺少宣传和提倡，本刊同人不忖棉力，纠合工商界同志，为这个主义而奋斗，感觉我们的期望一天比一天的殷切，但我们的责任也一天比一天的加重，我们不能够怠惰了，所以本刊就毅然出版，谨将我们的目的布露于右：经的方面  增强国防经济壁垒，促进战时工商努力，表扬国货优质，沟通工商学术，联络工商感情。纬的方面  唤起共同努力战时生产，普遍推行国货消费。我们微弱的呼声，能否达到这种目的，我们决不考虑，新生的本刊，现在比方一颗撒在田里的谷芽，我们却是农夫，谷芽长成的茁壮与否和结穗的丰盛与否，全在农夫耕耘的努力，所以我们自信不会失望，而同情于我们宗旨的比方是雨露，自然我们很需要他们的扶植，给这颗谷茁以滋润，独是我们棉力有限，还望各界诸君指导，海内外名流巨子，如果能够惠赐鸿篇巨著，尤所欢迎。（1940年创刊号,第2页）', '0', '191', '1', '', '[\"工商月刊\"]', '[\"A0119\",\"A0302\"]', 'G', '', '刊期不详', '大公出版社编辑 [并发行]', '香港', '00307/113', '1940年创刊于香港，由大公出版社编辑并发行。停刊时间不详。属于工商业刊物。主要撰稿人有苏国山、杜其章、任渭访等。设有社会小说、特写、插图故事等栏目。该刊物旨在沟通工商学术，联络工商感情，提倡支持国货。载文报道当时国货厂商情况，介绍香港各行商业概况。“我们的目的布告于右：经的方面：增强国防经济壁垒，促进战时工商努力，表扬国货优质，沟通工商学术，联络工商感情；纬的方面：唤起共同努力战时生产，普遍推行国货消费”。刊发有《从爱国说到提倡国货》、《工商业预算论》、《欧战中香港金融之面面观》、《爱国龙舟歌，工商第一声》、《提倡国货急不容缓》、《从日本的危机说到提倡国货》、《从爱国说到提倡国货》等文介绍战时香港工商业的状况和受到的影响及今后发展的出路，应当立足于自力更生；《战区及香港国货准予运内地推销》、《香港铁价飞涨》、《欧战爆发英印绵纱来源锐减沪纱激增日图夺销场》、《日封锁我沿海后行业界损失七千万元》、《马来西亚市场繁荣，畅销我国货物》、《广州大闹米荒中日伪垄断居奇》等众多新闻报道反映随着抗战及二战的规模扩大对香港及其他地区工商业造成了巨大影响；《黄金白银输美统计》、《英国出入口货统计》、《去年香港各行业概况》、《二月份香港日常用品价格》、《去年香港物价指数》等文系统统计了香港及与香港有关地区的工商业发展情况的数据。该刊物对于研究抗战时