INSERT INTO `ea_guji_books` VALUES ('1782', '0b172b5641ccccca96dbec489b85c2f5', '11', '268', '268', '527', '胜利版画', '胜利版画', '', '[\"J227\"]', '1943', 'C:\\Users\\Administrator\\Desktop\\hello_word\\sources', '', '', '1942年8月1日创刊于重庆，月刊，16开本，土纸本，由英国驻华大使馆新闻处编辑发行，该刊属于木刻刊物，同年8月出版了第三期后停刊，具体停刊原因不详，现馆藏1942年出版的第1至3期内容。《胜利版画》由梅健鹰担任主编，该刊全部刊登木刻作品，该刊的创办宗旨在于昭示中国与英美两国共同作战，促成民主国家反侵略阵线之最后胜利以及中国艺术家在战时的贡献。主要内容为反映抗日及盟军作战的大量的木刻作品，也包含风景画，并配有解说文字。该刊向读者昭示了中国艺术家战时的贡献，用精彩的木刻反映中国不屈不挠的精神。值得注意的是该刊在刊尾印有“同盟国家联合起来决心铲除轴心俾使全世界人民得以永享自由”字样。《胜利版画》刊载的作品有梅健鹰的《英首相丘吉尔》、《争取最后胜利》、《盟机轰炸日本》，谭旻的《中国空军美国志愿团》，夏伟图的《重庆跳伞塔》，尤玉英的《八铳高射机关炮》，力夫的《中国战时大学》、《喷火式战斗机击落德机》，白云的《澳洲牧场》，俊昶的《保福式轰炸机》，谭勇的《军民合作》、《英勇射手》，杨若英的《怀故乡》等，这些作品全部都是黑白木刻。《胜利版画》作为木刻刊物，刊登的作品反映出当时时代背景下的艺术表现形式，对于我们了解上世纪三、四十年代中国木刻艺术的发展具有很高的参考价值。', '0', '191', '1', '', '[\"胜利版画\"]', '[\"A0130\"]', '', '', '月刊', '英国驻华大使馆新闻处编辑 [并发行]', '重庆', 'GT0225/0225', '1942年8月1日创刊于重庆，月刊，16开本，土纸本，由英国驻华大使馆新闻处编辑发行，该刊属于木刻刊物，同年8月出版了第三期后停刊，具体停刊原因不详，现馆藏1942年出版的第1至3期内容。《胜利版画》由梅健鹰担任主编，该刊全部刊登木刻作品，该刊的创办宗旨在于昭示中国与英美两国共同作战，促成民主国家反侵略阵线之最后胜利以及中国艺术家在战时的贡献。主要内容为反映抗日及盟军作战的大量的木刻作品，也包含风景画，并配有解说文字。该刊向读者昭示了中国艺术家战时的贡献，用精彩的木刻反映中国不屈不挠的精神。值得注意的是该刊在刊尾印有“同盟国家联合起来决心铲除轴心俾使全世界人民得以永享自由”字样。《胜利版画》刊载的作品有梅健鹰的《英首相丘吉尔》、《争取最后胜利》、《盟机轰炸日本》，谭旻的《中国空军美国志愿团》，夏伟图的《重庆跳伞塔》，尤玉英的《八铳高射机关炮》，力夫的《中国战时大学》、《喷火式战斗机击落德机》，白云的《澳洲牧场》，俊昶的《保福式轰炸机》，谭勇的《军民合作》、《英勇射手》，杨若英的《怀故乡》等，这些作品全部都是黑白木刻。《胜利版画》作为木刻刊物，刊登的作品反映出当时时代背景下的艺术表现形式，对于我们了解上世纪三、四十年代中国木刻艺术的发展具有很高的参考价值。', null, null, null);
