INSERT INTO `ea_guji_books` VALUES ('1814', '0b9fc3f75b78b114dbcbb0165a690f07', '11', '268', '268', '527', '渌江月刊', '渌江月刊', '', '[\"C55\"]', '1946', 'C:\\Users\\Administrator\\Desktop\\hello_word\\sources', '', '', '社科综合性刊物。本刊旨在宣传三民主义，号召广大青年挑起建国巨担。主要内容分专辑、论述、文艺等栏目。载文有“伟大的船长”、“复员与复原”、“渌江桥畔的状元洲”等。', '0', '191', '1', '', '[\"渌江月刊\"]', '[\"A0126\"]', 'L', '', '月刊', '三民主义青年团湖南支团醴陵分团第一区队部编辑 [并发行]', '湖南', '17372/67203', '社科综合性刊物。本刊旨在宣传三民主义，号召广大青年挑起建国巨担。主要内容分专辑、论述、文艺等栏目。载文有“伟大的船长”、“复员与复原”、“渌江桥畔的状元洲”等。', null, null, null);
INSERT INTO `ea_guji_books` VALUES ('1815', '0ba1552e1a70841b1166245cff8e9058', '11', '268', '268', '527', '上海潮洲和济医院第十期廿九年全年收支报告', '上海潮洲和济医院第十期廿九年全年收支报告', '上海潮洲和济医院 [发行]', '[\"R197.3\"]', '1941', 'C:\\Users\\Administrator\\Desktop\\hello_word\\sources', '', '', '19??年在上海创刊，停刊于1941年第10期。由上海潮州和济医院主办、发行。刊期不详，属于医院报告刊物。 该刊载文记载上海潮洲和济医院1940年逐月收支报告、免费病人姓名、欠费病人姓名、支出收入各项总目等内容。上海潮洲和济医院简称潮州医院，为潮州旅沪同乡会公立，实行财政公开，具有慈善性质，接受各方捐款。刊首公布医院详细地址及医院在公共租界和法租界的紧急电话，收支报告分为月捐统计及逐月收付分项统计。捐助包括特捐、补助金、移助捐及年捐，除公布民国二十九年一月到十二月的月捐统计数字之外，还补充刊发了民国二十八年十二月份的月捐统计。而逐月收付分项统计也详细统计了从该年一月份至十二月份的收入款和支出款。除上述详细的收支报告外，该刊还公布了本期免费病人姓名及欠费病人姓名，附有第八期欠费无力归还将付免费病人姓名及第九期欠费病人姓名，以敦促欠款人或其家属及时上交，以免导致本院缺乏医药费用，贻误其他病人。《廿九年一月份起至十二月份止全年收支总结录》则汇总了该年度的收支情况，主要从“本期收入各项总目”、 “本期支出各项总目”及结后的财政状况三个方面展开。最后，该刊还以列表的方式详细地介绍了上海潮州和济医院，包括其组织构成及其主要负责人姓名，地址电话，诊治科目、时间、案例等，还详细说明了该医院的慈善性质，公开财政状况以便接受监督，也接受社会捐款，并提醒各界人士在捐款和到院就诊时应注意的问题。', '0', '191', '1', '', '[\"上海潮洲和济医院第十期廿九年全年收支报告\"]', '[\"A0119\"]', 'S', '上海潮洲和济医院 [发行]', '刊期不详', '编辑者不详', '上海', '15517/60650', '19??年在上海创刊，停刊于1941年第10期。由上海潮州和济医院主办、发行。刊期不详，属于医院报告刊物。 该刊载文记载上海潮洲和济医院1940年逐月收支报告、免费病人姓名、欠费病人姓名、支出收入各项总目等内容。上海潮洲和济医院简称潮州医院，为潮州旅沪同乡会公立，实行财政公开，具有慈善性质，接受各方捐款。刊首公布医院详细地址及医院在公共租界和法租界的紧急电话，收支报告分为月捐统计及逐月收付分项统计。捐助包括特捐、补助金、移助捐及年捐，除公布民国二十九年一月到十二月的月捐统计数字之外，还补充刊发了民国二十八年十二月份的月捐统计。而逐月收付分项统计也详细统计了从该年一月份至十二月份的收入款和支出款。除上述详细的收支报告外，该刊还公布了本期免费病人姓名及欠费病人姓名，附有第八期欠费无力归还将付免费病人姓名及第九期欠费病人姓名，以敦促欠款人或其家属及时上交，以免导致本院缺乏医药费用，贻误其他病人。《廿九年一月份起至十二月份止全年收支总结录》则汇总了该年度的收支情况，主要从“本期收入各项总目”、 “本期支出各项总目”及结后的财政状况三个方面展开。最后，该刊还以列表的方式详细地介绍了上海潮州和济医院，包括其组织构成及其主要负责人姓名，地址电话，诊治科目、时间、案例等，还详细说明了该医院的慈善性质，公开财政状况以便接受监督，也接受社会捐款，并提醒各界人士在捐款和到院就诊时应注意的问题。', null, null, null);
