INSERT INTO `ea_guji_books` VALUES ('1820', '0ba7c7790d794796e057a848fa8d8abb', '11', '268', '268', '527', '活教育', '活教育', '陈鹤琴主编;  中华儿童教育社出版', '', '0', 'C:\\Users\\Administrator\\Desktop\\hello_word\\sources', '', '', '教育刊物。旨在改进国民教育，辅导教师进修，提供具体教材，讨论实际方法。发表教育学论著，探讨儿童教育的方法，根据儿童的需要进行教学、训育研究，教法、教材研究。倡导推行“活教育”的理念，设有教育讲座，研究国外儿童教育方面的问题，同时报道教育界消息。', '0', '191', '1', '', '[\"活教育\"]', '[\"A0111\"]', 'H', '陈鹤琴主编;  中华儿童教育社出版', '月刊', '', '江西泰和,?', '上图(6302)3067; 6302/22483-84,87', '教育刊物。旨在改进国民教育，辅导教师进修，提供具体教材，讨论实际方法。发表教育学论著，探讨儿童教育的方法，根据儿童的需要进行教学、训育研究，教法、教材研究。倡导推行“活教育”的理念，设有教育讲座，研究国外儿童教育方面的问题，同时报道教育界消息。', null, null, null);
