INSERT INTO `ea_guji_books` VALUES ('1859', '0c55794e4f3800b3caab7ed74db7e3dc', '11', '268', '268', '527', '广大之路', '广大之路', '', '[\"G645.5\"]', '1933', 'C:\\Users\\Administrator\\Desktop\\hello_word\\sources', '', '', '学生刊物。本刊以研究科学原理，阐扬学术思想，讨论实际问题，发挥救国理论，提高文艺意识、以贡献与社会国家为宗旨。同时希望全中国全世界广大的群众都朝这广大之路走来，共同到达“人群进化，世界大同”的境界。主要内容有论著、研究、翻译、述评、介绍、读书录、文艺等。', '0', '191', '1', '', '[\"广大之路\"]', '[\"A0124\",\"A0302\"]', 'G', '', '半年刊', '广州大学学生自治会出版委员会编辑 [并出版]', '广州', '11917/41996_97', '学生刊物。本刊以研究科学原理，阐扬学术思想，讨论实际问题，发挥救国理论，提高文艺意识、以贡献与社会国家为宗旨。同时希望全中国全世界广大的群众都朝这广大之路走来，共同到达“人群进化，世界大同”的境界。主要内容有论著、研究、翻译、述评、介绍、读书录、文艺等。', null, null, null);
INSERT INTO `ea_guji_books` VALUES ('1860', '0c572f1a9adbb81262c75caea09e81bd', '11', '268', '268', '527', '劳工状况', '劳工状况', '青岛特别市公署社会局 [发行]', '[\"F249.275.23\"]', '1939', 'C:\\Users\\Administrator\\Desktop\\hello_word\\sources', '', '', '1938年9月创刊于青岛，年刊，每年九月出版。由青岛特别市公署社会局负责发行，青岛特别市社会局厚生科劳工股负责编辑，青岛德民印刷局[ 青岛市史志办公室编：《青岛市志——轻工业志/建材工业志》，北京：新华出版社，2000年，第31页。]负责印刷。该刊属于地方劳动经济统计刊物。停刊时间及原因不详，现馆藏1939年出版的第2期及1940年出版的第3期。该刊发行目的在于为工商、经济、社会教育、卫生等部门及各类工厂提供参考信息。主要刊载青岛市各业职工及一般劳动者状况。该刊将劳工的男女人数、工厂的工资、生活状况等用数字统计出来，同时在纺织工业类，化学工业类之下又分染织业，颜料业等小类，将生产工厂的名称、工人数目、日给工资、工作时间等用数字表格统计。主要分设栏目有纺织工业类、化学工业类、机器及金属品类、冶炼工业类、交通用具制造类、建筑工程类、公用事业类、服用品制造类、饮食品及烟草制造类、制纸印刷业类、饰物文具仪器制造类、其他工业类等等。《劳工状况》作为抗日战争战略相持阶段在日本侵略者占据的沦陷区出版发行的经济统计类刊物，采用了政府部门职工状况调查的基本数据，因而保留了这一时期青岛市劳工及社会经济的一些基本信息，对于全面了解青岛工业在日本占领时期的情况具有一定的参考意义。', '0', '191', '1', '', '[\"劳工状况\"]', '[\"A0130\"]', '', '', '年刊', '社会局厚生科劳工股编辑', '青岛', 'GT0388/0388', '1938年9月创刊于青岛，年刊，每年九月出版。由青岛特别市公署社会局负责发行，青岛特别市社会局厚生科劳工股负责编辑，青岛德民印刷局[ 青岛市史志办公室编：《青岛市志——轻工业志/建材工业志》，北京：新华出版社，2000年，第31页。]负责印刷。该刊属于地方劳动经济统计刊物。停刊时间及原因不详，现馆藏1939年出版的第2期及1940年出版的第3期。该刊发行目的在于为工商、经济、社会教育、卫生等部门及各类工厂提供参考信息。主要刊载青岛市各业职工及一般劳动者状况。该刊将劳工的男女人数、工厂的工资、生活状况等用数字统计出来，同时在纺织工业类，化学工业类之下又分染织业，颜料业等小类，将生产工厂的名称、工人数目、日给工资、工作时间等用数字表格统计。主要分设栏目有纺织工业类、化学工业类、机器及金属品类、冶炼工业类、交通用具制造类、建筑工程类、公用事业类、服用品制造类、饮食品及烟草制造类、制纸印刷业类、饰物文具仪器制造类、其他工业类等等。《劳工状况》作为抗日战争战略相持阶段在日本侵略者占据的沦陷区出版发行的经济统计类刊物，采用了政府部门职工状况调查的基本数据，因而保留了这一时期青岛市劳工及社会经济的一些基本信息，对于全面了解青岛工业在日本占领时期的情况具有一定的参考意义。', null, null, null);
