INSERT INTO `ea_guji_books` VALUES ('1889', '0cf50607ea7b135dea41c3f405b43eda', '11', '268', '268', '527', '唯美', '唯美', '王蔚然,钦佐颺 [发行人]', '[\"J121\"]', '1935', 'C:\\Users\\Administrator\\Desktop\\hello_word\\sources', '', '', '艺术刊物。本刊以各种艺术作品，包括美术照相、金石、书画、电影人物，雕塑、建筑、陶瓷等为主，还有不少艺术介绍文章。刊有“张充仁个展特辑”。', '0', '191', '1', '', '[\"唯美\",\"The Art Classics\"]', '[\"A0120\"]', 'W', '王蔚然,钦佐颺 [发行人]', '月刊', '邵无斋,柴骋陆编辑', '上海', '17545/67670; 7102', '艺术刊物。本刊以各种艺术作品，包括美术照相、金石、书画、电影人物，雕塑、建筑、陶瓷等为主，还有不少艺术介绍文章。刊有“张充仁个展特辑”。', null, null, null);
