INSERT INTO `ea_guji_books` VALUES ('1907', '0d366880a338283418315e604d772a1f', '11', '268', '268', '527', '开发西北', '开发西北', '开发西北协会编辑;  正中书局 [发行]', '[\"F129.6\"]', '0', 'C:\\Users\\Administrator\\Desktop\\hello_word\\sources', '', '', '1934年1月创刊于南京，至1935年12月第4卷第6期，共发行4卷24期。由开发西北协会编辑，由正中书局发行。月刊，属于西北地方建设刊物。主要撰稿人有邵力子、杨缵绪、罗文干、黄伯逵、虞振镛、徐益仁、文萱、张中岳等。主要栏目有一月以来之西北、舆论选辑、会务报告、西北各省近况、西北调查报告等。该刊以“介绍西北实地情形，研讨开发西北计划，促进开发西北事业”为宗旨。本刊主要内容为探讨包括察哈尔、绥远、宁夏、甘肃、青海、新疆、陕西、西康、西藏、外蒙古等省、区在内的整个西北地区的开发计划，研究该地区政治、经济、文化教育，民族等各种实际问题，内容十分丰富。该刊作为开发西北协会的会刊，在两年的编辑发行时间内，较为详细地介绍了西北各省的情况。该刊每期均对西北地区一月之内新闻进行了报道，对了解1934至1935年西北地区各方面时事，有一定的参考价值。该刊对西北地区经济情况有所刊载，如《青省皮产调查》详细介绍分析了青海省皮产种类、皮市贸易情况以及存在的问题。该刊对当时西北地区存在诸多政治问题也十分关注，如中良所撰写的《新疆问题之剖视》论述了新疆的沿革、地理环境、近代以来外国侵略情况以及内在存在的问题，提出了以移民垦殖、文化建设等方式解决新疆问题。面对日本蚕食中国的现实，该刊对于日本侵略西北的野心也有所揭露，指出西北并非国人普遍认为的稳定的后方，而是急需加以建设方能抵御侵略的战略要地。此外，该刊保留了开发西北协会的会务报告等内容，对于认识该协会的历史有一定的参考价值。该刊刊载的有关西北风土人情的照片，对于认识20世纪30年代的西北实际面貌也具有一定的参考价值。', '0', '191', '1', '', '[\"开发西北\"]', '[\"A0112\"]', 'K', '开发西北协会编辑;  正中书局 [发行]', '月刊', '', '南京', '上图9843/35252-53', '1934年1月创刊于南京，至1935年12月第4卷第6期，共发行4卷24期。由开发西北协会编辑，由正中书局发行。月刊，属于西北地方建设刊物。主要撰稿人有邵力子、杨缵绪、罗文干、黄伯逵、虞振镛、徐益仁、文萱、张中岳等。主要栏目有一月以来之西北、舆论选辑、会务报告、西北各省近况、西北调查报告等。该刊以“介绍西北实地情形，研讨开发西北计划，促进开发西北事业”为宗旨。本刊主要内容为探讨包括察哈尔、绥远、宁夏、甘肃、青海、新疆、陕西、西康、西藏、外蒙古等省、区在内的整个西北地区的开发计划，研究该地区政治、经济、文化教育，民族等各种实际问题，内容十分丰富。该刊作为开发西北协会的会刊，在两年的编辑发行时间内，较为详细地介绍了西北各省的情况。该刊每期均对西北地区一月之内新闻进行了报道，对了解1934至1935年西北地区各方面时事，有一定的参考价值。该刊对西北地区经济情况有所刊载，如《青省皮产调查》详细介绍分析了青海省皮产种类、皮市贸易情况以及存在的问题。该刊对当时西北地区存在诸多政治问题也十分关注，如中良所撰写的《新疆问题之剖视》论述了新疆的沿革、地理环境、近代以来外国侵略情况以及内在存在的问题，提出了以移民垦殖、文化建设等方式解决新疆问题。面对日本蚕食中国的现实，该刊对于日本侵略西北的野心也有所揭露，指出西北并非国人普遍认为的稳定的后方，而是急需加以建设方能抵御侵略的战略要地。此外，该刊保留了开发西北协会的会务报告等内容，对于认识该协会的历史有一定的参考价值。该刊刊载的有关西北风土人情的照片，对于认识20世纪30年代的西北实际面貌也具有一定的参考价值。', null, null, null);
