INSERT INTO `ea_guji_books` VALUES ('1949', '0ded21bfe8d004e586cc0ea25ede6e1d', '11', '268', '268', '527', '政务周刊', '政务周刊', '', '[\"D693.62\"]', '1933', 'C:\\Users\\Administrator\\Desktop\\hello_word\\sources', '', '', '本刊为豫鄂皖三省勦匪总司令部刊物，为宣达政令互资观摩、指导豫鄂皖三省政务改进而发行。', '0', '191', '1', '', '[\"政务周刊\"]', '[\"A0120\"]', 'Z', '', '旬刊,周刊', '豫鄂皖三省勦匪总司令部秘书处编辑 [并发行]', '汉口', '06674/23558_61', '本刊为豫鄂皖三省勦匪总司令部刊物，为宣达政令互资观摩、指导豫鄂皖三省政务改进而发行。', null, null, null);
