INSERT INTO `ea_guji_books` VALUES ('2004', '0edb3305547aeb06d2c853f41aa3576f', '11', '268', '268', '527', '铲共月刊', '铲共月刊', '', '[\"D23\"]', '1937', 'C:\\Users\\Administrator\\Desktop\\hello_word\\sources', '', '', '政治刊物。发表反共言论，宣传铲共与抗俄，刊有赣南、湘鄂、闽东等地区共产党活动的通讯，国内外反共消息，平津学潮与各校动静。', '0', '191', '1', '', '[\"铲共月刊\"]', '[\"A0124\"]', 'C', '', '月刊', '铲共月刊社编辑 [并发行]', '天津', 'GWJ0380/0380', '政治刊物。发表反共言论，宣传铲共与抗俄，刊有赣南、湘鄂、闽东等地区共产党活动的通讯，国内外反共消息，平津学潮与各校动静。', null, null, null);
INSERT INTO `ea_guji_books` VALUES ('2005', '0ede6f1f057cb7225cad5c5be9741f81', '11', '268', '268', '527', '石室公教月刊', '石室公教月刊', '广州市石室天主堂 [发行]', '[\"B975\"]', '1929', 'C:\\Users\\Administrator\\Desktop\\hello_word\\sources', '', '', '《石室公教月刊》于1929年8月创刊于广州，1932年3月第32期停刊。由《120010》继承。广州市石室天主堂出版发行。月刊，属于石室天主教会的宣传刊物。本刊通篇采用竖版繁体字，主题是宣传天主教会。刊文有《圣玛大肋纳瞻礼之日默想》、《续论诵念圣经》、《古祖寿考》、《信德出自耳功》等文，这些文章都是以宣传天主教会以及阐释自己对于天主教的理解之类的文章，如《古祖寿考》这篇文章是对基督教的先祖亚当、厄诺等人的生辰进行的论述。《信德出自耳功》这篇文章阐述了作者认为信仰基督教信字出自耳闻。除此之外，本刊刊有中华公教信友进行会广州总会职员和中华公教信友进行会广东清远分会职员的肖像图片，如中华公教信友进行会广州总会的胡心澄，中华公教信友进行会广东清远分会会长刘耀荣的肖像，这些肖像不仅对于研究中华公教信友进行会保存了珍贵的图片，另外作为二十世纪二十年代的人物肖像图，也有一定的范本作用，为我们了解时人服饰文化也有一定的参考价值。本刊还保留一部分来函，如《广东清远公教进行会支会来函》等，作为当时广东清远分会来函原件，为我们研究当时该分会的活动提供了原始资料。大量教义在该刊的阐释，它的刊行必将有利于基督教的传播。其次，它的刊行为后人了解广州石室天主教提供了参考依据。最后，作为一份天主教会的刊物，也为我们研究二十世纪二十年代的基督教在中国的传播提供了范本。', '0', '191', '1', '', '[\"石室公教月刊\"]', '[\"A0119\",\"A0302\"]', 'S', '广州市石室天主堂 [发行]', '月刊', '编辑者不详', '广州', '16188/63512_28', '《石室公教月刊》于1929年8月创刊于广州，1932年3月第32期停刊。由《120010》继承。广州市石室天主堂出版发行。月刊，属于石室天主教会的宣传刊物。本刊通篇采用竖版繁体字，主题是宣传天主教会。刊文有《圣玛大肋纳瞻礼之日默想》、《续论诵念圣经》、《古祖寿考》、《信德出自耳功》等文，这些文章都是以宣传天主教会以及阐释自己对于天主教的理解之类的文章，如《古祖寿考》这篇文章是对基督教的先祖亚当、厄诺等人的生辰进行的论述。《信德出自耳功》这篇文章阐述了作者认为信仰基督教信字出自耳闻。除此之外，本刊刊有中华公教信友进行会广州总会职员和中华公教信友进行会广东清远分会职员的肖像图片，如中华公教信友进行会广州总会的胡心澄，中华公教信友进行会广东清远分会会长刘耀荣的肖像，这些肖像不仅对于研究中华公教信友进行会保存了珍贵的图片，另外作为二十世纪二十年代的人物肖像图，也有一定的范本作用，为我们了解时人服饰文化也有一定的参考价值。本刊还保留一部分来函，如《广东清远公教进行会支会来函》等，作为当时广东清远分会来函原件，为我们研究当时该分会的活动提供了原始资料。大量教义在该刊的阐释，它的刊行必将有利于基督教的传播。其次，它的刊行为后人了解广州石室天主教提供了参考依据。最后，作为一份天主教会的刊物，也为我们研究二十世纪二十年代的基督教在中国的传播提供了范本。', null, null, null);
