INSERT INTO `ea_guji_books` VALUES ('2008', '0ee4603d0673c838cd74ba358ce31893', '11', '268', '268', '527', '教育旬刊(福建)', '教育旬刊(福建)', '', '[\"G527\"]', '1931', 'C:\\Users\\Administrator\\Desktop\\hello_word\\sources', '', '', '教育刊物。该刊的宗旨称：“根据三民主义以充实人民生活扶植社会生存，发展国民生计，延续生命为目的；务期民族独立，民权普遍，民生发展，以促进世界大同”。载文有“乡村教师应当做的两件事”、“四区教育改进的我见”等。', '0', '191', '1', '', '[\"教育旬刊(福建)\"]', '[\"A0120\"]', 'J', '', '旬刊', '南安县诗淘码鹏四区教育委员会教育旬刊编辑社编辑 [并发行]', '南安(福建)', '08625/31824', '教育刊物。该刊的宗旨称：“根据三民主义以充实人民生活扶植社会生存，发展国民生计，延续生命为目的；务期民族独立，民权普遍，民生发展，以促进世界大同”。载文有“乡村教师应当做的两件事”、“四区教育改进的我见”等。', null, null, null);
