INSERT INTO `ea_guji_books` VALUES ('2010', '0ee84186d7c3257d4ffbc1d26305d572', '11', '268', '268', '527', '上海法政大学校刊', '上海法政大学校刊', '上海法政大学出版', '[\"G648.2\"]', '1928', 'C:\\Users\\Administrator\\Desktop\\hello_word\\sources', '', '', '大学校刊。1928年2月创办于上海，由上海政法大学发行。该刊的刊期和停刊时间皆不详，馆藏第1期。该刊以讨论学术、商榷艺文为使命，主要记载学校一切行政事宜，同时刊登开学典礼、校务简录以及学校动态，设有记事和新闻栏目。“记事”栏目主要刊载该校开学典礼、校务会议、校长就职等重大事项的记录，载文《二月十三日本校举行开学典礼略记》、《杨代校长就职记事》、《十七年二月十八日第一次嫌恶委员会简录》、《欢送郑校长欢迎杨代校长及新教授纪事》和《本校轇轕之前因后果》等。“新闻”栏目主要报道该校教务近闻、校历、入学报到、图书馆书以及校务等方面的新闻，载有《教务处近闻一束》、《作息时刻表》、《十六年度第二学期本校校历》、《十六年度第二学期周数表》、《新旧生入学手续》、《图书馆新到大批书籍》、《整顿校舍之余闻》等，其中，《教务处近闻一束》报道了该校的教务安排、休假信息、班次、学科等方面的消息。该刊还刊登了作息时间表、周数表等表格，其中表格《鍗号说明》对该校的各种铃声信号的指示信息做出了详细的解释。该刊有助于考察民国时期上海政法大学的日常教学和行政情况，并从侧面反映出当时大学教育发展水平。', '0', '191', '1', '', '[\"上海法政大学校刊\"]', '[\"A0130\"]', '', '', '[刊期不详]', '编辑单位不详', '上海', '00775/3170', '大学校刊。1928年2月创办于上海，由上海政法大学发行。该刊的刊期和停刊时间皆不详，馆藏第1期。该刊以讨论学术、商榷艺文为使命，主要记载学校一切行政事宜，同时刊登开学典礼、校务简录以及学校动态，设有记事和新闻栏目。“记事”栏目主要刊载该校开学典礼、校务会议、校长就职等重大事项的记录，载文《二月十三日本校举行开学典礼略记》、《杨代校长就职记事》、《十七年二月十八日第一次嫌恶委员会简录》、《欢送郑校长欢迎杨代校长及新教授纪事》和《本校轇轕之前因后果》等。“新闻”栏目主要报道该校教务近闻、校历、入学报到、图书馆书以及校务等方面的新闻，载有《教务处近闻一束》、《作息时刻表》、《十六年度第二学期本校校历》、《十六年度第二学期周数表》、《新旧生入学手续》、《图书馆新到大批书籍》、《整顿校舍之余闻》等，其中，《教务处近闻一束》报道了该校的教务安排、休假信息、班次、学科等方面的消息。该刊还刊登了作息时间表、周数表等表格，其中表格《鍗号说明》对该校的各种铃声信号的指示信息做出了详细的解释。该刊有助于考察民国时期上海政法大学的日常教学和行政情况，并从侧面反映出当时大学教育发展水平。', null, null, null);
INSERT INTO `ea_guji_books` VALUES ('2011', '0ee8fce962b74248093e1206976e92d3', '11', '268', '268', '527', '海潮(奉贤)', '海潮(奉贤)', '奉贤南桥海潮出版社 [发行]', '[\"I216.1\"]', '1945', 'C:\\Users\\Administrator\\Desktop\\hello_word\\sources', '', '', '文学刊物。本刊旨在放出海阔天空的眼界，达到海宇澄清的境界，介绍新颖的思潮，引导正当的思潮。主要内容分短评、小说、新体诗、转载、科学常识、随笔等。', '0', '191', '1', '', '[\"海潮(奉贤)\"]', '[\"A0126\"]', 'H', '奉贤南桥海潮出版社 [发行]', '半月刊', '胡慈培主编', '奉贤(江苏)', '07714/28294', '文学刊物。本刊旨在放出海阔天空的眼界，达到海宇澄清的境界，介绍新颖的思潮，引导正当的思潮。主要内容分短评、小说、新体诗、转载、科学常识、随笔等。', null, null, null);
