INSERT INTO `ea_guji_books` VALUES ('2070', '0fe1e8903c6f94a6fe48eabb1c970157', '11', '268', '268', '527', '上海市私立开明函授学校学员俱乐部', '上海市私立开明函授学校学员俱乐部', '', '[\"G727\"]', '1933', 'C:\\Users\\Administrator\\Desktop\\hello_word\\sources', '', '', '19??年在上海创刊，馆藏收录1933年第6期和第8期。由上海市私立开明函授学校编辑并发行。季刊，属于青年刊物。该刊主要撰稿人不详，主要设有我们的学员、学员写作、公开信箱、问答选刊、学员录等栏目。其中，前两个栏目主要刊登学员的照片及学员的文艺作品，公开信箱和问答选刊则主要解答学生遇到的难题，为其提出解决办法。上海市私立开明函授学校创立的宗旨是：使有志上进之失学者，得以在职务余暇，修习中学程度之各种学科；同时使在校就学之学生，获得课外修业补益校课之机会。为使学员之间联络感情，交流知识，每三个月印行《学员俱乐部》一册，分赠参加的学员。该刊主要教授自学方法，解答学员疑惑，发表该校学员作品，介绍经典书目，发表读书感想，刊载学校学员录、该校通告等内容。该刊还载文探讨学习问题，如英语学习方法、英语发音、鸦片战争的背景、生活中的物理问题、趣味物理现象等问题；也有介绍墨水的制造方法、作文的批改实例等。《文学的力量》、《我的学习英文的失败》、《日常生活中的物理学问题》、《作文批改实例》等是其中较重要的文章。1933年第8期为“失学问题专号”，除刊登学员照片、学员的文艺作品之外，刊有《自学的阶段》、《关于写作的两种误会的见解》、《实验室——昆虫采集和制作标本的经过》等重要文章。本刊对于加强学员之间的联系，促进学习交流起到了一定作用。', '0', '191', '1', '', '[\"上海市私立开明函授学校学员俱乐部\"]', '[\"A0119\"]', 'S', '', '季刊', '开明函授学校编辑 [并发行]', '上海', '15465/60539_40', '19??年在上海创刊，馆藏收录1933年第6期和第8期。由上海市私立开明函授学校编辑并发行。季刊，属于青年刊物。该刊主要撰稿人不详，主要设有我们的学员、学员写作、公开信箱、问答选刊、学员录等栏目。其中，前两个栏目主要刊登学员的照片及学员的文艺作品，公开信箱和问答选刊则主要解答学生遇到的难题，为其提出解决办法。上海市私立开明函授学校创立的宗旨是：使有志上进之失学者，得以在职务余暇，修习中学程度之各种学科；同时使在校就学之学生，获得课外修业补益校课之机会。为使学员之间联络感情，交流知识，每三个月印行《学员俱乐部》一册，分赠参加的学员。该刊主要教授自学方法，解答学员疑惑，发表该校学员作品，介绍经典书目，发表读书感想，刊载学校学员录、该校通告等内容。该刊还载文探讨学习问题，如英语学习方法、英语发音、鸦片战争的背景、生活中的物理问题、趣味物理现象等问题；也有介绍墨水的制造方法、作文的批改实例等。《文学的力量》、《我的学习英文的失败》、《日常生活中的物理学问题》、《作文批改实例》等是其中较重要的文章。1933年第8期为“失学问题专号”，除刊登学员照片、学员的文艺作品之外，刊有《自学的阶段》、《关于写作的两种误会的见解》、《实验室——昆虫采集和制作标本的经过》等重要文章。本刊对于加强学员之间的联系，促进学习交流起到了一定作用。', null, null, null);
