INSERT INTO `ea_guji_books` VALUES ('2075', '0ff53efabe13c683dcbe40091198bffb', '11', '268', '268', '527', '浙江省中等教育研究会季刊', '浙江省中等教育研究会季刊', '浙江省中等教育研究会编辑 [并出版]', '', '0', 'C:\\Users\\Administrator\\Desktop\\hello_word\\sources', '', '', '教育刊物。主要研究中等教育的教学法，以增进青年期教育的效率，内容包括国文教学法的研究，中学教育的方针以及教材的选用和教学情况调查。栏目有：论著、研究、讲演等。', '0', '191', '1', '', '[\"浙江省中等教育研究会季刊\"]', '[\"A0111\"]', 'Z', '浙江省中等教育研究会编辑 [并出版]', '季刊', '', '杭州', '上图(7514)3571;7514/27342', '教育刊物。主要研究中等教育的教学法，以增进青年期教育的效率，内容包括国文教学法的研究，中学教育的方针以及教材的选用和教学情况调查。栏目有：论著、研究、讲演等。', null, null, null);
INSERT INTO `ea_guji_books` VALUES ('2076', '0ff550acf2f3ec8283d65ebc7dd24287', '11', '268', '268', '527', '湖北特教半月刊', '湖北特教半月刊', '湖北省特种教育处编辑 [并发行]', '[\"G76\"]', '0', 'C:\\Users\\Administrator\\Desktop\\hello_word\\sources', '', '', '1935年1月创刊于湖北武昌，1935年8月第3卷第4期停刊。该刊由湖北省特种教育处编辑、发行，程其保题写刊名。逐页题名《特教半月刊》。属于半月刊，属于教育类刊物。主要撰稿人有邱焕、王流光、杨春芳、杨远翔、谢绍雄等人。主要设有论著、法令、公文、工作报告、特载、调查及统计、通讯、研究等栏目。其中，特载论著和法令公文较为重要。特别是论著一栏，涉及内容颇多，探讨问题较为全面。该刊主要以宣扬三民主义，阐扬特教理论及其实施方案，揭破赤匪罪恶，提倡新生活，启发民族意识为宗旨。其内容不仅包括湖北省特种教育处处务日记、会议记录、工作报告，还包括相关法令法规、公牍训令和调查报告等诸多方面，并大力探讨农村复兴、学校兴建、特种教育与挽救国难等具体问题，宣传救国教育的主张。同时也刊有诽谤、咒骂共产党的言论。该刊刊有《民主与独裁》、《特种教育与挽救国难》（陆学贤）、《特种教育的意义与使命》（姜伯韩）、《中国之农村建设》（李怪）、《现代教育趋势》（经筱川）、《中国普及教育方案之商榷》等重要文章，阐述了特种教育的现状、特种教育与挽救国难、特种教育与民族复兴、特种教育与农村建设、特种教育与新生活等系列问题。该刊内容较丰富，且紧密联系湖北特种教育的现状，所探讨的问题具有实践意义，也可作为研究当时国共关系的参考资料。', '0', '191', '1', '', '[\"湖北特教半月刊\"]', '[\"A0112\"]', 'H', '湖北省特种教育处编辑 [并发行]', '半月刊', '', '武昌(湖北)', '上图9527/34522', '1935年1月创刊于湖北武昌，1935年8月第3卷第4期停刊。该刊由湖北省特种教育处编辑、发行，程其保题写刊名。逐页题名《特教半月刊》。属于半月刊，属于教育类刊物。主要撰稿人有邱焕、王流光、杨春芳、杨远翔、谢绍雄等人。主要设有论著、法令、公文、工作报告、特载、调查及统计、通讯、研究等栏目。其中，特载论著和法令公文较为重要。特别是论著一栏，涉及内容颇多，探讨问题较为全面。该刊主要以宣扬三民主义，阐扬特教理论及其实施方案，揭破赤匪罪恶，提倡新生活，启发民族意识为宗旨。其内容不仅包括湖北省特种教育处处务日记、会议记录、工作报告，还包括相关法令法规、公牍训令和调查报告等诸多方面，并大力探讨农村复兴、学校兴建、特种教育与挽救国难等具体问题，宣传救国教育的主张。同时也刊有诽谤、咒骂共产党的言论。该刊刊有《民主与独裁》、《特种教育与挽救国难》（陆学贤）、《特种教育的意义与使命》（姜伯韩）、《中国之农村建设》（李怪）、《现代教育趋势》（经筱川）、《中国普及教育方案之商榷》等重要文章，阐述了特种教育的现状、特种教育与挽救国难、特种教育与民族复兴、特种教育与农村建设、特种教育与新生活等系列问题。该刊内容较丰富，且紧密联系湖北特种教育的现状，所探讨的问题具有实践意义，也可作为研究当时国共关系的参考资料。', null, null, null);
