INSERT INTO `ea_guji_books` VALUES ('2113', '10647f4fd49d815eb45e9a5bbb97a560', '11', '268', '268', '527', '抗敌', '抗敌', '', '[\"K265\"]', '1937', 'C:\\Users\\Administrator\\Desktop\\hello_word\\sources', '', '', '抗战刊物。除了在“时评”、“战情一周”等固定栏目随时报道战况外，着重对日本帝国主义侵华暴行进行揭露，对抗战形势及抗战中存在的各种问题进行分析，对世界局势的发展及其对中国战局的影响发表评论，呼吁全民抗战。', '0', '191', '1', '', '[\"抗敌\"]', '[\"A0120\"]', 'K', '', '周刊', '抗敌周刊社编辑 [并发行]', '成都', '04803/17909', '抗战刊物。除了在“时评”、“战情一周”等固定栏目随时报道战况外，着重对日本帝国主义侵华暴行进行揭露，对抗战形势及抗战中存在的各种问题进行分析，对世界局势的发展及其对中国战局的影响发表评论，呼吁全民抗战。', null, null, null);
INSERT INTO `ea_guji_books` VALUES ('2114', '1066ea596e5b4e0a7c62160bb70e9c4b', '11', '268', '268', '527', '世界画报', '世界画报', '', '[\"C55\"]', '1946', 'C:\\Users\\Administrator\\Desktop\\hello_word\\sources', '', '', '1946年5月1日在上海创刊，停刊日期不详。世界画报社编辑、发行。刊期不详，属于综合性画报。主要撰稿人有芝郎、郎兰、鉴非、同春、高鸣、白洲、茅平、胡立祥、叶上材、木人、陈瑛、丁杏、石光等，主要栏目不详。该刊主要刊载国内外明星生活轶事，报导最新时尚消息，介绍奇风异俗等。刊内发有大量明星照片。载有《李绮年玩弄男明星》、《米高梅开摄肉感片》、《电影明星的没落，朱秋痕不堪回首》、《后方红星王豪在上海》、《轰动纽约荧屏的新片：喜剧<为了他>的故事》等我国以及世界电影界的消息，刊发明星照片。另外，该刊对舞场内容的报道也较多，如《舞女爱国仇敌——怒撕裂太阳旗》、《舞场点滴》、《春宵之舞》等。除此之外，该刊还登载《美国时装大王》、《上海美兵回乡后大穿绣花装》、《狄梵艳事》、《笑话奇谈》、《李长江，这妙人》、《回头是岸胡适发狠劲》、《梁漱溟消极退出政治舞台》、《张北生潦倒时拾过香烟屁股》、《茅盾的童年和家庭》、《陈立夫妙语如珠》等社会名人的现状与趣事，增加该刊的趣味性。除了登载照片之外，漫话也是该刊的重要内容，刊有《爸爸的秘密》、《家庭艺术家》、《新发明》、《惟女人与小人难养也》等，发表短篇小说《胡导演传》。该刊内容丰富，图片与文字结合，增加了该刊的可读性，内容也选取人们所感兴趣的社会新闻与名人事件，具有浓厚的小市民气息。', '0', '191', '1', '', '[\"世界画报\"]', '[\"A0119\"]', 'S', '', '刊期不详', '[世界画报]编辑 [并发行]', '上海', '16172/6968', '1946年5月1日在上海创刊，停刊日期不详。世界画报社编辑、发行。刊期不详，属于综合性画报。主要撰稿人有芝郎、郎兰、鉴非、同春、高鸣、白洲、茅平、胡立祥、叶上材、木人、陈瑛、丁杏、石光等，主要栏目不详。该刊主要刊载国内外明星生活轶事，报导最新时尚消息，介绍奇风异俗等。刊内发有大量明星照片。载有《李绮年玩弄男明星》、《米高梅开摄肉感片》、《电影明星的没落，朱秋痕不堪回首》、《后方红星王豪在上海》、《轰动纽约荧屏的新片：喜剧<为了他>的故事》等我国以及世界电影界的消息，刊发明星照片。另外，该刊对舞场内容的报道也较多，如《舞女爱国仇敌——怒撕裂太阳旗》、《舞场点滴》、《春宵之舞》等。除此之外，该刊还登载《美国时装大王》、《上海美兵回乡后大穿绣花装》、《狄梵艳事》、《笑话奇谈》、《李长江，这妙人》、《回头是岸胡适发狠劲》、《梁漱溟消极退出政治舞台》、《张北生潦倒时拾过香烟屁股》、《茅盾的童年和家庭》、《陈立夫妙语如珠》等社会名人的现状与趣事，增加该刊的趣味性。除了登载照片之外，漫话也是该刊的重要内容，刊有《爸爸的秘密》、《家庭艺术家》、《新发明》、《惟女人与小人难养也》等，发表短篇小说《胡导演传》。该刊内容丰富，图片与文字结合，增加了该刊的可读性，内容也选取人们所感兴趣的社会新闻与名人事件，具有浓厚的小市民气息。', null, null, null);
