INSERT INTO `ea_guji_books` VALUES ('2138', '10cec10d7462c52bf9abe93c434a330b', '11', '268', '268', '527', '考核汇刊', '考核汇刊', '党政工作考核委员会秘书处编辑 [并发行]', '', '0', 'C:\\Users\\Administrator\\Desktop\\hello_word\\sources', '', '', '政务刊物。刊载有关党政工作考核的论著、法令等，带有一定的指导意义。内容包含对党政机关以及党政工作人员的考核方针、意义、方法、范围、制度、操作以及对考核工作本身的检讨，另有国外对考核与督察制度的介绍。栏目有：特载、论著、记述、法令与附录。', '0', '191', '1', '', '[\"考核汇刊\"]', '[\"A0109\"]', 'K', '党政工作考核委员会秘书处编辑 [并发行]', '不定期', '', '重庆', '上图4004/15196-98', '政务刊物。刊载有关党政工作考核的论著、法令等，带有一定的指导意义。内容包含对党政机关以及党政工作人员的考核方针、意义、方法、范围、制度、操作以及对考核工作本身的检讨，另有国外对考核与督察制度的介绍。栏目有：特载、论著、记述、法令与附录。', null, null, null);
INSERT INTO `ea_guji_books` VALUES ('2139', '10d9b847bd55c61081ed625bbd084a0a', '11', '268', '268', '527', '新儿童杂志', '新儿童杂志', '', '[\"G61\"]', '1935', 'C:\\Users\\Administrator\\Desktop\\hello_word\\sources', '', '', '1935年1月在上海创刊，该刊由中华儿童用品社编辑并发行，季刊，属于儿童刊物，是研究儿童教育重要资料。该刊主要撰稿人有余萍客、陆镐周、倪文亚、周乐山、杨卫玉、潘仰尧、李永福等。本刊不分栏目。   《新儿童杂志》以“培养儿童良好品行，增进儿童智力，促进儿童健康”为宗旨，以改进儿童生活，谋求社会福利、民族发展为目的。本刊主要刊载有关儿童的科学教养，儿童生活常识，儿童生活用品指南等内容。在这一期中，刊有社会各界名人关于儿童教育的题词，如于右任、孙科、何应钦、李宗仁等。该刊曾刊载过《儿童教育的先决条件》、《儿童的五个时期》、《儿童用品要注意感觉和运动中枢的训练》、《儿童之教育与民族进化》、《儿童教育的根本问题》、《儿童的生活与玩具》、《儿童教育用品选择标准》、《儿童玩具制造应有之认识》等文章。该刊为研究儿童教育，了解儿童成长与生活用品关系提供了一定的资料。', '0', '191', '1', '', '[\"新儿童杂志\"]', '[\"A0117\"]', 'X', '', '季刊', '中华儿童用品社编辑 [并发行]', '上海', '10667/5037', '1935年1月在上海创刊，该刊由中华儿童用品社编辑并发行，季刊，属于儿童刊物，是研究儿童教育重要资料。该刊主要撰稿人有余萍客、陆镐周、倪文亚、周乐山、杨卫玉、潘仰尧、李永福等。本刊不分栏目。   《新儿童杂志》以“培养儿童良好品行，增进儿童智力，促进儿童健康”为宗旨，以改进儿童生活，谋求社会福利、民族发展为目的。本刊主要刊载有关儿童的科学教养，儿童生活常识，儿童生活用品指南等内容。在这一期中，刊有社会各界名人关于儿童教育的题词，如于右任、孙科、何应钦、李宗仁等。该刊曾刊载过《儿童教育的先决条件》、《儿童的五个时期》、《儿童用品要注意感觉和运动中枢的训练》、《儿童之教育与民族进化》、《儿童教育的根本问题》、《儿童的生活与玩具》、《儿童教育用品选择标准》、《儿童玩具制造应有之认识》等文章。该刊为研究儿童教育，了解儿童成长与生活用品关系提供了一定的资料。', null, null, null);
